const inputData = $input.first().json.message.content;
const dateString = inputData.invoiceDate;
const parsedDate = new Date(dateString);
const monthOptions = { month: 'long' };
const monthName = parsedDate.toLocaleDateString('de-DE', monthOptions);
const yearValue = parsedDate.getFullYear().toString();
const dayValue = String(parsedDate.getDate()).padStart(2, '0');
const monthValue = String(parsedDate.getMonth() + 1).padStart(2, '0');
const germanDate = dayValue + '.' + monthValue + '.' + yearValue;
const outputData = {
  company: inputData.company,
  description: inputData.description,
  invoiceNumber: inputData.invoiceNumber,
  netAmount: inputData.netAmount,
  taxRatePercent: inputData.taxRatePercent,
  taxAmount: inputData.taxAmount,
  grossAmount: inputData.grossAmount,
  currency: inputData.currency,
  invoiceDate: inputData.invoiceDate,
  datumDeutsch: germanDate,
  monat: monthName,
  jahr: yearValue
};
return [outputData];

const currentItem = $input.item;
const binaryData = currentItem.binary || {};
const resultItems = [];
for (const [keyName, binaryContent] of Object.entries(binaryData)) {
  const newItem = {
    json: { _sourceAttachmentKey: keyName },
    binary: { data: binaryContent }
  };
  resultItems.push(newItem);
}
return resultItems;
