// Code node in n8n
// Loops through all input items and parses the "Mensaje" field
return items.map(item => {
  let parsed = {};
  try {
    parsed = JSON.parse(item.json.Mensaje);
  } catch (error) {
    // If parsing fails, log it and continue
    parsed = { error: 'Invalid JSON', raw: item.json.Mensaje };
  }

  return {
    json: parsed
  };
});
