// Calculate patient age from date of birth
const items = $input.all();
const results = [];

for (const item of items) {
  const dob = new Date(item.json.date_of_birth);
  const today = new Date();
  let age = today.getFullYear() - dob.getFullYear();
  const monthDiff = today.getMonth() - dob.getMonth();

  if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < dob.getDate())) {
    age--;
  }

  // Determine patient category
  let patientCategory = 'adult';
  if (age < 2) patientCategory = 'infant';
  else if (age < 12) patientCategory = 'child';
  else if (age < 18) patientCategory = 'adolescent';
  else if (age >= 65) patientCategory = 'senior';

  results.push({
    json: {
      ...item.json,
      patient_age: age,
      patient_category: patientCategory
    }
  });
}

return results;

