// n8n Code Node - Transform Merged ClickUp + GHL Data & Aggregate

const transformedItems = [];

// Process each item from the merged input
for (const item of $input.all()) {
  const data = item.json;

  // Check if this is a ClickUp task (has custom_id, creator, project fields)
  // or a GHL opportunity (has pipelineId, monetaryValue, contactId)
  const isClickUpTask = data.hasOwnProperty('custom_id') && 
                        data.hasOwnProperty('creator') && 
                        data.hasOwnProperty('project');

  if (isClickUpTask) {
    // Transform ClickUp task to GHL opportunity format
    const transformedItem = {
      // Use ClickUp task ID as the opportunity ID
      id: data.id,

      // Use task name as opportunity name
      name: data.name,

      // Set default monetary value (you can extract from custom fields if available)
      monetaryValue: extractMonetaryValue(data.custom_fields),

      // Map ClickUp project to pipeline ID
      pipelineId: data.project?.id || "YOUR_PIPELINE_ID",

      // Map ClickUp status to pipeline stage
      pipelineStageId: data.status?.id || null,
      pipelineStageUId: data.status?.id || null,

      // Map first assignee ID
      assignedTo: data.assignees?.[0]?.id?.toString() || null,

      // Map ClickUp status type to GHL status
      status: mapClickUpStatusToGHL(data.status?.type),

      // Source information
      source: "ClickUp",

      // Timestamps - convert from milliseconds to ISO string
      lastStatusChangeAt: data.date_updated ? new Date(parseInt(data.date_updated)).toISOString() : null,
      lastStageChangeAt: data.date_updated ? new Date(parseInt(data.date_updated)).toISOString() : null,
      createdAt: data.date_created ? new Date(parseInt(data.date_created)).toISOString() : null,
      updatedAt: data.date_updated ? new Date(parseInt(data.date_updated)).toISOString() : null,

      // Index version
      indexVersion: 1,

      // Use first assignee as contact ID
      contactId: data.assignees?.[0]?.id?.toString() || null,

      // Location from team ID
      locationId: data.team_id || "YOUR_LOCATION_ID",

      // Custom fields
      customFields: data.custom_fields || [],

      // Lost reason
      lostReasonId: null,

      // Followers from watchers
      followers: data.watchers?.map(w => w.id.toString()) || [],

      // Relations - create from assignees
      relations: data.assignees?.map((assignee, index) => ({
        associationId: "OPPORTUNITIES_CONTACTS_ASSOCIATION",
        relationId: data.id,
        primary: index === 0,
        objectKey: "contact",
        recordId: assignee.id.toString(),
        fullName: assignee.username,
        contactName: assignee.username,
        companyName: data.project?.name || null,
        email: assignee.email,
        phone: null,
        tags: data.tags?.map(t => t.name) || [],
        attributed: null
      })) || [],

      // Primary contact from first assignee
      contact: data.assignees?.[0] ? {
        id: data.assignees[0].id.toString(),
        name: data.assignees[0].username,
        companyName: data.project?.name || null,
        email: data.assignees[0].email,
        phone: null,
        tags: data.tags?.map(t => t.name) || [],
        score: []
      } : null,

      // Sort fields
      sort: [
        parseInt(data.date_created),
        data.assignees?.[0]?.id?.toString() || ""
      ],

      // Attributions
      attributions: [{
        utmSessionSource: "ClickUp",
        isFirst: true,
        medium: "integration"
      }]
    };

    transformedItems.push(transformedItem);

  } else {
    // This is already a GHL opportunity - pass it through as-is
    transformedItems.push(data);
  }
}

// Helper function to map ClickUp status types to GHL status
function mapClickUpStatusToGHL(statusType) {
  if (!statusType) return 'open';

  const statusMap = {
    'closed': 'won',
    'complete': 'won',
    'done': 'won',
    'open': 'open',
    'in progress': 'open',
    'to do': 'open',
    'cancelled': 'abandoned',
    'blocked': 'abandoned',
    'lost': 'lost'
  };

  return statusMap[statusType.toLowerCase()] || 'open';
}

// Helper function to extract monetary value from custom fields
function extractMonetaryValue(customFields) {
  if (!customFields || customFields.length === 0) return 0;

  // Look for common field names that might contain monetary value
  const moneyField = customFields.find(f => 
    f.name && (
      f.name.toLowerCase().includes('value') ||
      f.name.toLowerCase().includes('amount') ||
      f.name.toLowerCase().includes('price') ||
      f.name.toLowerCase().includes('deal')
    )
  );

  return moneyField?.value || 0;
}

// AGGREGATION: Combine all transformed items into a single output
// This ensures the AI Agent receives ONE item with all data
const aggregatedOutput = {
  totalItems: transformedItems.length,
  timestamp: new Date().toISOString(),
  opportunities: transformedItems,
  summary: {
    totalOpportunities: transformedItems.length,
    byStatus: {},
    totalMonetaryValue: 0,
    bySource: {}
  }
};

// Calculate summary statistics
transformedItems.forEach(item => {
  // Count by status
  aggregatedOutput.summary.byStatus[item.status] = 
    (aggregatedOutput.summary.byStatus[item.status] || 0) + 1;

  // Sum monetary values
  aggregatedOutput.summary.totalMonetaryValue += item.monetaryValue || 0;

  // Count by source
  const source = item.source || 'GHL';
  aggregatedOutput.summary.bySource[source] = 
    (aggregatedOutput.summary.bySource[source] || 0) + 1;
});

// Return single aggregated item for AI Agent
return [{ json: aggregatedOutput }];

// Extract the output data from your input format
const inputData = $input.first().json;
const output = inputData.output;

// Create routing objects based on your end-of-day report structure
const slackReport = {
  type: 'slack',
  title: output.title,
  summary: output.briefSummary,
  wins: output.wins,
  keyMetrics: output.keyMetrics,
  blockers: output.blockers,
