// Get input data
const items = $input.all();
const cleanedItems = [];

for (const item of items) {
  const data = item.json;

  // Access the body object where the actual form data is
  const formData = data.body || data;

  // Normalize and clean data
  const cleaned = {
    name: formData.name?.trim() || 'Anonymous',
    email: formData.email?.trim().toLowerCase() || '',
    rating: parseInt(formData.rating) || 0,
    comments: formData.comments?.trim() || 'No comments provided',
    suggestions: formData.suggestions?.trim() || 'No suggestions provided',
    timestamp: new Date().toISOString(),
    submissionId: `FB-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
  };

  // Validate email format
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (cleaned.email && !emailRegex.test(cleaned.email)) {
    cleaned.email = '';
    cleaned.hasValidEmail = false;
  } else {
    cleaned.hasValidEmail = true;
  }

  cleanedItems.push({ json: cleaned });
}

return cleanedItems;

