const items = $input.all();
const outputItems = [];

for (let i = 0; i < items.length; i++) {
  const feedbackData = $node["Clean & Normalize Data1"].json;
  const aiResponse = items[i].json.message.content;

  let aiAnalysis;

  try {
    // Clean the response - remove any markdown code blocks if present
    let cleanedResponse = aiResponse.trim();

    // Remove markdown code block markers if they exist
    cleanedResponse = cleanedResponse.replace(/```json\n?/g, '');
    cleanedResponse = cleanedResponse.replace(/```\n?/g, '');
    cleanedResponse = cleanedResponse.trim();

    // Parse the JSON
    aiAnalysis = JSON.parse(cleanedResponse);

    // Validate that we have all required fields
    if (!aiAnalysis.sentiment || !aiAnalysis.highlights || !aiAnalysis.suggestions || !aiAnalysis.summary) {
      throw new Error("Missing required fields in AI response");
    }

    console.log("Successfully parsed AI response as JSON");

  } catch (e) {
    console.log("Failed to parse as JSON, using fallback:", e.message);

    // Fallback: Extract data intelligently from text
    let sentiment = "Neutral";
    const lowerResponse = aiResponse.toLowerCase();

    if (lowerResponse.includes("positive") || 
        lowerResponse.includes("great") || 
        lowerResponse.includes("excellent")) {
      sentiment = "Positive";
    } else if (lowerResponse.includes("negative") || 
               lowerResponse.includes("poor") || 
               lowerResponse.includes("disappointed")) {
      sentiment = "Negative";
    }

    // Create fallback structure
    aiAnalysis = {
      sentiment: sentiment,
      highlights: [
        feedbackData.comments || "Customer provided feedback",
        `Rating: ${feedbackData.rating}/5 stars`
      ],
      suggestions: [feedbackData.suggestions || "No specific suggestions"],
      summary: aiResponse.substring(0, 300)
    };
  }

  // Sentiment color mapping
  const sentimentColors = {
    'Positive': '#10b981',
    'Neutral': '#f59e0b',
    'Negative': '#ef4444'
  };

  // Build output object
  outputItems.push({
    json: {
      ...feedbackData,
      aiSummary: aiAnalysis.summary,
      sentiment: aiAnalysis.sentiment,
      sentimentColor: sentimentColors[aiAnalysis.sentiment] || '#6b7280',
      highlights: Array.isArray(aiAnalysis.highlights) ? aiAnalysis.highlights : [aiAnalysis.highlights],
      aiSuggestions: Array.isArray(aiAnalysis.suggestions) ? aiAnalysis.suggestions : [aiAnalysis.suggestions]
    }
  });
}

return outputItems;

