parameters.jsCode:
let customer = {
  exists: $input.first().json.data.length > 0,
  name: "",
  email: "",
  deal: {
    exists: false,
    details: {}
  }
}

if (customer.exists) {
  customer = {
    ...customer,
    name: $input.first().json.data[0].values.name[0].full_name,
    email: $input.first().json.data[0].values.email_addresses[0].email_address,
  }

  if ($input.first().json.data[0].values.associated_deals.length > 0) {
    customer = {
      ...customer,
      deal: {
        exists: true,
        details: $input.first().json.data[0].values.associated_deals[0]
      }
    }
  }
}

return customer;

