// Clean text
let cleanedText = pdfText
  .replace(/\f/g, '\n')
  .replace(/\n{3,}/g, '\n\n')
  .replace(/^\d+\s*$/gm, '')
  .replace(/PSYCHOLOGY SECONDARY COURSE/gi, '')
  .replace(/MODULE - I.*?Psychology/gis, '')
  .trim();

// Extract document metadata
const wordCount = cleanedText.split(/\s+/).length;
const charCount = cleanedText.length;
const estimatedMinutes = Math.ceil(charCount / 1000);

return {
