const input = $input.item.json;
const cleanedText = input.cleanedText;
const documentTitle = input.documentTitle;

const MAX_BYTES = 4500;

const sectionRegex = /(\d+\.\d+)\s+([A-Z][A-Z\s]{10,})/g;
let sections = [];
let lastIndex = 0;
let match;

while ((match = sectionRegex.exec(cleanedText)) !== null) {
  const sectionNumber = match[1];
  const sectionTitle = match[2].trim();
  const startIndex = match.index;

  if (lastIndex < startIndex) {
    const prevText = cleanedText.substring(lastIndex, startIndex).trim();
    if (prevText.length > 100) {
      sections.push({ text: prevText, number: sections.length + 1, title: 'Introduction' });
    }
  }

  sections.push({ number: sectionNumber, title: sectionTitle, startIndex });
  lastIndex = startIndex;
}

const episodes = [];
for (let i = 0; i < sections.length; i++) {
  const section = sections[i];
  const nextSection = sections[i + 1];
  const endIndex = nextSection ? nextSection.startIndex : cleanedText.length;
  const sectionText = cleanedText.substring(section.startIndex, endIndex).trim();

  if (Buffer.byteLength(sectionText, 'utf8') > MAX_BYTES) {
    const chunks = splitTextIntoChunks(sectionText, MAX_BYTES);
    chunks.forEach((chunk, idx) => {
      episodes.push({
        episodeNumber: `${section.number}.${idx + 1}`,
        episodeTitle: `${documentTitle} - Section ${section.number}.${idx + 1}: ${section.title}`,
        sectionTitle: section.title,
        textContent: chunk,
        charCount: chunk.length,
        totalEpisodes: null
      });
    });
  } else {
    episodes.push({
      episodeNumber: section.number,
      episodeTitle: `${documentTitle} - Section ${section.number}: ${section.title}`,
      sectionTitle: section.title,
      textContent: sectionText,
      charCount: sectionText.length,
      totalEpisodes: null
    });
  }
}

if (episodes.length === 0) {
  const chunks = splitTextIntoChunks(cleanedText, MAX_BYTES);
  chunks.forEach((chunk, idx) => {
    episodes.push({
      episodeNumber: idx + 1,
      episodeTitle: `${documentTitle} - Part ${idx + 1}`,
      sectionTitle: `Part ${idx + 1}`,
      textContent: chunk,
      charCount: chunk.length,
      totalEpisodes: chunks.length
    });
  });
}

episodes.forEach(ep => ep.totalEpisodes = episodes.length);

function splitTextIntoChunks(text, maxBytes) {
  const chunks = [];
  let currentChunk = '';
  const sentences = text.split(/(?<=[.!?])\s+/);

  for (const sentence of sentences) {
    const testChunk = currentChunk + (currentChunk ? ' ' : '') + sentence;
    if (Buffer.byteLength(testChunk, 'utf8') > maxBytes) {
      if (currentChunk) {
        chunks.push(currentChunk);
        currentChunk = sentence;
      } else {
        const words = sentence.split(' ');
        for (const word of words) {
          const testWord = currentChunk + (currentChunk ? ' ' : '') + word;
          if (Buffer.byteLength(testWord, 'utf8') > maxBytes) {
            chunks.push(currentChunk);
            currentChunk = word;
          } else {
            currentChunk = testWord;
          }
        }
      }
    } else {
      currentChunk = testChunk;
    }
  }

  if (currentChunk) {
    chunks.push(currentChunk);
  }

  return chunks;
}

return episodes.map(ep => ({ json: ep }));

