}

const currentUsage = monthlyUsage[monthKey].charCount;
const charCount = $input.item.json.charCount;
const MONTHLY_LIMIT = 950000;

if (currentUsage + charCount > MONTHLY_LIMIT) {
  throw new Error(
    `‚ö†Ô∏è MONTHLY TTS LIMIT REACHED!\n` +
    `Current: ${currentUsage.toLocaleString()} chars\n` +
    `This request: ${charCount.toLocaleString()} chars\n` +
    `Limit: ${MONTHLY_LIMIT.toLocaleString()} chars`
  );
}

return {
  json: {
    ...$input.item.json,
    usageInfo: {
      currentMonthUsage: currentUsage,
      thisRequestChars: charCount,
      remainingChars: MONTHLY_LIMIT - currentUsage - charCount,
      monthKey
    }
  }
};

