const allItems = $input.all();

const sortedItems = allItems.sort((a, b) => {
  const numA = a.json.episodeNumber || 0;
  const numB = b.json.episodeNumber || 0;
  return numA - numB;
});

const mp3Buffers = [];
let totalSize = 0;
let totalChars = 0;
const episodesList = [];

for (const item of sortedItems) {
  const audioData = item.json.audioContent;
  const buffer = Buffer.from(audioData, 'base64');
  mp3Buffers.push(buffer);
  totalSize += buffer.length;
  totalChars += item.json.charCount || 0;

  episodesList.push({
    number: item.json.episodeNumber,
    title: item.json.episodeTitle,
    charCount: item.json.charCount
  });
}

const combinedBuffer = Buffer.concat(mp3Buffers);
const combinedBase64 = combinedBuffer.toString('base64');

const date = new Date().toISOString().split('T')[0];
const fileName = `Chapter-2_Complete_${date}.mp3`;

return [{
  json: {
