// Parse incoming Jotform data
const data = items[0].json;

// Extract main fields
const name = `${data["Full Name"].first} ${data["Full Name"].last}`.trim();
const email = data["E-mail"];
const phone = data["Contact Number"]?.full || "";
const billing = data["Billing Address"];
const address = [billing.addr_line1, billing.addr_line2, billing.city, billing.state, billing.postal, billing.country]
  .filter(Boolean)
  .join(", ");

// Extract product details
const products = (data["My Products"].products || []).map(p => ({
  name: p.productName,
  quantity: p.quantity,
  unitPrice: p.unitPrice,
  subTotal: p.subTotal,
  currency: p.currency,
  options: p.productOptions.join(", "),
}));

// Extract total
const totalInfo = data["My Products"].totalInfo || {};
const total = totalInfo.totalSum || 0;
const currency = totalInfo.currency || "USD";

// Generate order ID - use placeholder prefix
const orderId = `{{ $order_id_prefix }}-${Math.floor(Date.now() / 1000)}`;

// Escape MarkdownV2 special chars for Telegram
const escapeMarkdown = (text) => {
  return text
    ?.toString()
    .replace(/([_*[\]()~`>#+=|{}.!-])/g, '\\$1');
};

// Prepare final object
return [
  {
    json: {
      orderId,
      customer: {
        name,
        email,
        phone,
        address,
      },
      products,
      total,
      currency,
      // Pre-generate summary text for other nodes (like Google Sheets)
      summaryText: `üß¥ *{{ $template_order_name }} Order Confirmation* 
Thank you, ${escapeMarkdown(name)}\\! Your order *#${escapeMarkdown(orderId)}* has been received ‚úÖ  

üõçÔ∏è *Products:* 
${products.map(p => `\\- ${escapeMarkdown(p.name)} x${escapeMarkdown(p.quantity)} \\(${escapeMarkdown(p.subTotal)} ${escapeMarkdown(p.currency)}\\)`).join('\n')}

üí∞ *Total:* ${escapeMarkdown(total)} ${escapeMarkdown(currency)}  
üìç *Address:* ${escapeMarkdown(address || "N/A")}  
üìû *Phone:* ${escapeMarkdown(phone || "N/A")}`,
    },
  },
];
