// Extract key HTML elements for analysis
const html = $json.body || '';
const url = $('Initialize Variables').item.json.landing_page_url;

// Extract title
const titleMatch = html.match(/<title[^>]*>([^<]+)<\/title>/i);
const pageTitle = titleMatch ? titleMatch[1].trim() : 'No title found';

// Extract meta description
const metaDescMatch = html.match(/<meta[^>]*name=["']description["'][^>]*content=["']([^"']+)["']/i);
const metaDescription = metaDescMatch ? metaDescMatch[1] : 'No meta description';

// Extract headings
const h1Matches = html.match(/<h1[^>]*>([^<]+)<\/h1>/gi) || [];
const h1s = h1Matches.map(h => h.replace(/<[^>]*>/g, '').trim()).slice(0, 5);

const h2Matches = html.match(/<h2[^>]*>([^<]+)<\/h2>/gi) || [];
const h2s = h2Matches.map(h => h.replace(/<[^>]*>/g, '').trim()).slice(0, 10);

// Extract CTAs (buttons and links with common CTA text)
const ctaPattern = /(get started|buy now|sign up|subscribe|download|learn more|contact|book|try|start|join)/gi;
const buttonMatches = html.match(/<button[^>]*>([^<]+)<\/button>/gi) || [];
const linkMatches = html.match(/<a[^>]*>([^<]+)<\/a>/gi) || [];
const allCTAs = [...buttonMatches, ...linkMatches]
  .map(cta => cta.replace(/<[^>]*>/g, '').trim())
  .filter(text => ctaPattern.test(text))
  .slice(0, 10);

// Count forms
const formCount = (html.match(/<form/gi) || []).length;

// Check for common frameworks/tools
const hasReact = html.includes('react');
const hasBootstrap = html.includes('bootstrap');
const hasTailwind = html.includes('tailwind');
const hasGA = html.includes('google-analytics') || html.includes('gtag');
const hasFBPixel = html.includes('facebook');

// Extract word count (rough estimate)
const textContent = html.replace(/<script[^>]*>.*?<\/script>/gi, '')
  .replace(/<style[^>]*>.*?<\/style>/gi, '')
  .replace(/<[^>]*>/g, ' ')
  .replace(/\s+/g, ' ')
  .trim();
const wordCount = textContent.split(/\s+/).length;

// Check mobile viewport
const hasMobileViewport = html.includes('viewport');

// Extract images
const imgMatches = html.match(/<img[^>]*>/gi) || [];
const imageCount = imgMatches.length;
const hasAltTags = imgMatches.filter(img => /alt=["'][^"']+["']/i.test(img)).length;

return {
  url: url,
  page_title: pageTitle,
  meta_description: metaDescription,
  h1_headings: h1s,
  h2_headings: h2s,
  cta_buttons: allCTAs,
  form_count: formCount,
  word_count: wordCount,
  image_count: imageCount,
  images_with_alt: hasAltTags,
  has_mobile_viewport: hasMobileViewport,
  detected_tech: {
    react: hasReact,
    bootstrap: hasBootstrap,
    tailwind: hasTailwind,
    google_analytics: hasGA,
    facebook_pixel: hasFBPixel
  },
  html_preview: html.substring(0, 3000),
  analysis_ready: true
};

