// Get data from the correct nodes using their names
const contactData = $('Get Contacts from Google Sheet').item.json;
const tableData = $('Get Template Structure from Table').item.json;

// Parse the template structure from our n8n Table
const templateStructure = JSON.parse(tableData.components_structure);

let finalComponents = [];

// Loop through the template structure to build all components
for (const component of templateStructure) {

  // --- NEW: HANDLES DYNAMIC IMAGE HEADERS ---
  if (component.type === 'HEADER') {
      let headerParams = [];
      // Check if the template expects a dynamic image
      if (component.format === 'IMAGE') {
          headerParams.push({
              type: 'image',
              image: {
                  link: contactData['Marketing Image URL'] // Get URL from the new sheet column
              }
          });
      }
      // Add more logic here for VIDEO or DOCUMENT if you use them in the future
      finalComponents.push({ type: 'header', parameters: headerParams });
  }

  // Handles dynamic body
  if (component.type === 'BODY' && component.text && component.text.includes('{{1}}')) {
    finalComponents.push({ 
      type: 'body', 
      parameters: [{ type: 'text', text: contactData['Full Name'] }] 
    });
  }

  // Handles all button types
  if (component.type === 'BUTTONS') {
    let urlButtonIndex = 0;
    for (const button of component.buttons) {
      if (button.type === 'URL' && button.url && button.url.includes('{{1}}')) {
        finalComponents.push({ 
          type: 'button', 
          sub_type: 'url', 
          index: String(urlButtonIndex),
          parameters: [{ 
            type: 'payload', 
            payload: String(contactData['Phone Number']) 
          }] 
        });
        urlButtonIndex++;
      }
    }
  }
}

// Build the final API request body
const finalApiRequestBody = {
  messaging_product: "whatsapp",
  to: String(contactData['Phone Number']),
  type: "template",
  template: {
    name: tableData.template_name,
    language: {
      code: tableData.language_code
    },
    components: finalComponents
  }
};

// Return this single object for the HTTP Request node
return {
  json: {
    finalApiRequestBody: finalApiRequestBody
  }
};

