// Filter only tweets that have text and an image
return items
  .map(item => {
    return {
      json: {
        author_name: item.json.author_name,
        author_username: item.json.author_username,
        tweet_text: item.json.tweet_text,
        tweet_url: item.json.tweet_url,
        tweet_id: item.json.tweet_id,
        created_at: item.json.created_at,
        image_url: item.json.first_image_url // retrieve the image link
      }
    };
  })
  .filter(item => item.json.tweet_text && item.json.image_url); // only keeps tweets with text AND images

// Filter and clean tweets
return $input.all()
  .map(item => {
    let text = item.json.Contenu;

    // Removes links
    text = text.replace(/https?:\/\/\S+/g, '').trim();

    return {
      json: {
        author_name: item.json["Nom du compte"],
        author_username: item.json["Nom d'utilisateur"],
        tweet_text: text,
        tweet_url: item.json.URL,
        tweet_id: item.json["ID Tweet"],
        created_at: item.json.Date,
        first_image_url: item.json.Image,
      }
    };
  })
  // Only keep tweets that have text and an image
  .filter(item => item.json.tweet_text && item.json.first_image_url);
