const item = $input.first().json;

try {
  // Get AI response
  const response = item.choices[0].message.content;

  // Clean JSON
  let cleanJson = response.replace(/```json\n?|```\n?/g, '').trim();
  const jsonMatch = cleanJson.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    cleanJson = jsonMatch[0];
  }

  const aiData = JSON.parse(cleanJson);

  // Get original data
  const originalData = $('Extract Onboarding Data').first().json;

  return [{
    json: {
      ...originalData,
      ...aiData
    }
  }];
} catch (error) {
  // Fallback
  const originalData = $('Extract Onboarding Data').first().json;
  return [{
    json: {
      ...originalData,
      priority_level: 'standard',
      onboarding_weeks: 4,
      key_goals: ['Complete orientation', 'Meet team', 'Learn systems'],
      training_needed: ['Company overview', 'Department training'],
      tools_required: ['Email', 'Slack', 'Project management']
    }
  }];
}
