// Safely get the Gmail HTML body
const html = $('Launch workflow on email receive').first().json.textAsHtml || "";

// --- CLEAN TEXT FROM HTML ---

// Step 1: Normalize line breaks for <p>, <br>, and <div> tags
let clean = html
  .replace(/<(?:br|div)\s*\/?>/gi, '\n')
  .replace(/<\/p>/gi, '\n');

// Step 2: Remove anchor tags but keep inner text
clean = clean.replace(/<a[^>]*>(.*?)<\/a>/gi, '$1');

// Step 3: Remove scripts, styles, and comments
clean = clean
  .replace(/<script[\s\S]*?<\/script>/gi, '')
  .replace(/<style[\s\S]*?<\/style>/gi, '')
  .replace(/<!--[\s\S]*?-->/g, '');

// Step 4: Remove <img> tags and Gmail-style [image: ...] placeholders
clean = clean
  .replace(/<img[^>]*>/gi, '')
  .replace(/\[image:\s*.*?\]/gi, '');

// Step 5: Strip remaining HTML tags
clean = clean.replace(/<[^>]+>/g, ' ');

// Step 6: Remove plain text URLs
clean = clean.replace(/\b(?:https?:\/\/|ftp:\/\/|www\.|mailto:)\S+\b/gi, '');

// Step 7: Decode basic HTML entities
clean = clean
  .replace(/&nbsp;/gi, ' ')
  .replace(/&amp;/gi, '&')
  .replace(/&lt;/gi, '<')
  .replace(/&gt;/gi, '>')
  .replace(/&quot;/gi, '"')
  .replace(/&#39;/gi, "'");

// Step 8: Clean up whitespace
clean = clean
  .replace(/\r/g, '')
  .replace(/[ \t]+\n/g, '\n')
  .replace(/\n{3,}/g, '\n\n')
  .replace(/\s+/g, ' ')
  .trim();

// Step 9: Remove potentially problematic symbols and quotes
clean = clean
  .replace(/['"`]/g, '')          // remove single, double, and back quotes
  .replace(/[<>\\]/g, '')         // remove angle brackets and backslashes
  .replace(/[{}[\]]/g, '')        // remove braces and brackets
  .replace(/[^\x20-\x7E\n]/g, ''); // remove non-printable characters (safe ASCII only)

// --- RETURN CLEAN TEXT AS OUTPUT ---

return [
  {
    json: {
      cleanText: clean,
    },
  },
];

// Get raw input
let rawString = $input.first().json.output;

// Clean up markdown formatting if present
rawString = rawString
  .trim()
  .replace(/^```(json)?/i, '')  // remove leading ``` or ```json
  .replace(/```$/i, '')         // remove trailing ```
  .trim();

// Try parsing the cleaned string
let parsed;
try {
  parsed = JSON.parse(rawString);
} catch (error) {
  throw new Error(`Failed to parse JSON. Cleaned input was:\n${rawString}\n\nError: ${error.message}`);
}

return parsed;
