// Get input data from previous nodes
const items = $input.all();
const results = [];

// Process each item
for (const item of items) {
  // Get file size from binary data
  const fileSize = item.binary?.data?.fileSize || 0;

  // Calculate different size units
  const fileSizeKB = Math.round(fileSize / 1024);
  const fileSizeMB = Math.round(fileSize / (1024 * 1024) * 100) / 100;

  // Get current date/time
  const now = new Date();
  const currentDate = now.toISOString().split('T')[0]; // YYYY-MM-DD format

  // Build result object
  const result = {
    // Keep original data
    app_name: item.json.app_name,
    version: item.json.version,
    build_number: item.json.build_number,
    ipa_url: item.json.ipa_url,

    // Add new calculated data
    date: currentDate,
    timestamp: now.toISOString(),
    file_size_bytes: fileSize,
    file_size_kb: fileSizeKB,
    file_size_mb: fileSizeMB
  };

  // Add to results array
  results.push(result);

  // Log for debugging (check browser console)
  console.log(`üìä ${item.json.app_name}: ${fileSizeMB} MB`);
}

// Return results to next node
return results;

// Simple trend analysis using thresholds
const items = $input.all();
const results = [];

for (const item of items) {
  const currentSize = item.json.file_size_mb;

  // Define size thresholds (you can customize these)
  const SMALL_APP = 50;   // MB
  const MEDIUM_APP = 150; // MB
  const LARGE_APP = 300;  // MB

  // Determine size category
  let sizeCategory = 'UNKNOWN';
  let trendStatus = 'NEW_TRACKING'; // First time tracking
  let alertLevel = 'INFO';

  if (currentSize < SMALL_APP) {
    sizeCategory = 'SMALL';
  } else if (currentSize < MEDIUM_APP) {
    sizeCategory = 'MEDIUM';
  } else if (currentSize < LARGE_APP) {
    sizeCategory = 'LARGE';
  } else {
    sizeCategory = 'VERY_LARGE';
    alertLevel = 'WARNING'; // Large apps always get warnings
  }

  // Check if size is unusually large (basic threshold check)
  if (currentSize > 500) {
    trendStatus = 'SIZE_ALERT_LARGE';
    alertLevel = 'CRITICAL';
  } else if (currentSize > 300) {
    trendStatus = 'SIZE_WARNING';
    alertLevel = 'WARNING';
  } else {
    trendStatus = 'SIZE_NORMAL';
  }

  const result = {
    ...item.json,
    size_category: sizeCategory,
    trend_status: trendStatus,
    alert_level: alertLevel,
    analysis_timestamp: new Date().toISOString(),
    analysis_note: `App size is ${sizeCategory.toLowerCase()} (${currentSize} MB)`
  };

  results.push(result);

  console.log(`üìä Analysis: ${item.json.app_name} = ${currentSize}MB (${sizeCategory})`);
}

return results;
