parameters.jsCode:
// Merge AI analysis with request data
const requestData = $input.first().json;
const aiAnalysisRaw = requestData.aiAnalysis;

// Parse AI analysis
let aiAnalysis;
try {
  aiAnalysis = JSON.parse(aiAnalysisRaw);
} catch (e) {
  // Fallback if parsing fails
  aiAnalysis = {
    issueCategory: {
      primary: 'general_repair',
      secondary: ['maintenance'],
      confidence: 60
    },
    priorityAssessment: {
      level: 'normal',
      reasoning: 'Standard maintenance request',
      keywords_detected: [],
      safety_concern: false,
      habitability_issue: false
    },
    estimatedComplexity: {
      level: 'moderate',
      estimated_hours: 2,
      requires_specialist: false
    },
    recommendedVendor: {
      type: 'handyman',
      urgency: 'within_24h',
      specialization_needed: 'General repair'
    },
    cost_estimate: {
      range: '$100 - $300',
      factors: ['Labor', 'Materials']
    },
    tenant_communication: {
      initial_response: 'We have received your request and are assigning a technician.',
      estimated_resolution: '24-48 hours',
      temporary_solutions: []
    },
    safety_protocols: ['Standard safety procedures'],
    recurring_issue_check: {
      likely_recurring: false,
      warranty_applicable: false,
      preventive_recommendation: 'Regular maintenance'
    },
    contractor_instructions: ['Assess issue', 'Provide quote if major repair needed'],
    legal_compliance: {
      urgent_legal_requirement: false,
      habitability_law_concern: false,
      documentation_needed: 'Before/after photos'
    }
  };
}

// Determine work order priority
const priorityMap = {
  'emergency': 1,
  'urgent': 2,
  'high': 3,
  'normal': 4,
  'low': 5
};

// Select contractor based on vendor type
const contractorDatabase = {
  'plumber': { name: 'QuickFix Plumbing', phone: '+1-555-PLUMB', email: 'user@example.com' },
  'electrician': { name: 'Bright Spark Electric', phone: '+1-555-SPARK', email: 'user@example.com' },
  'hvac_tech': { name: 'Cool Breeze HVAC', phone: '+1-555-HVAC1', email: 'user@example.com' },
  'handyman': { name: 'All-Fix Handyman', phone: '+1-555-FIX-IT', email: 'user@example.com' },
  'locksmith': { name: '24/7 Locksmith', phone: '+1-555-LOCKS', email: 'user@example.com' },
  'pest_control': { name: 'No-Bug Pest Control', phone: '+1-555-BUGS0', email: 'user@example.com' },
  'general_contractor': { name: 'BuildRight Contractors', phone: '+1-555-BUILD', email: 'user@example.com' }
};

const vendorType = aiAnalysis.recommendedVendor.type;
const contractor = contractorDatabase[vendorType] || contractorDatabase['handyman'];

return {
  json: {
    ...requestData,
    issueCategory: aiAnalysis.issueCategory.primary,
    subCategories: aiAnalysis.issueCategory.secondary,
    aiConfidence: aiAnalysis.issueCategory.confidence,
    finalPriority: aiAnalysis.priorityAssessment.level,
    priorityNumber: priorityMap[aiAnalysis.priorityAssessment.level] || 4,
    priorityReasoning: aiAnalysis.priorityAssessment.reasoning,
    keywordsDetected: aiAnalysis.priorityAssessment.keywords_detected,
    safetyConcern: aiAnalysis.priorityAssessment.safety_concern,
    habitabilityIssue: aiAnalysis.priorityAssessment.habitability_issue,
    complexity: aiAnalysis.estimatedComplexity.level,
    estimatedHours: aiAnalysis.estimatedComplexity.estimated_hours,
    requiresSpecialist: aiAnalysis.estimatedComplexity.requires_specialist,
    vendorType: vendorType,
    vendorUrgency: aiAnalysis.recommendedVendor.urgency,
    specialization: aiAnalysis.recommendedVendor.specialization_needed,
    costEstimate: aiAnalysis.cost_estimate.range,
    costFactors: aiAnalysis.cost_estimate.factors,
    tenantMessage: aiAnalysis.tenant_communication.initial_response,
    estimatedResolution: aiAnalysis.tenant_communication.estimated_resolution,
    temporarySolutions: aiAnalysis.tenant_communication.temporary_solutions,
    safetyProtocols: aiAnalysis.safety_protocols,
    likelyRecurring: aiAnalysis.recurring_issue_check.likely_recurring,
    warrantyApplicable: aiAnalysis.recurring_issue_check.warranty_applicable,
    preventiveRecommendation: aiAnalysis.recurring_issue_check.preventive_recommendation,
    contractorInstructions: aiAnalysis.contractor_instructions,
    legalUrgent: aiAnalysis.legal_compliance.urgent_legal_requirement,
    habitabilityLaw: aiAnalysis.legal_compliance.habitability_law_concern,
    documentationNeeded: aiAnalysis.legal_compliance.documentation_needed,
    assignedContractor: contractor.name,
    contractorPhone: contractor.phone,
    contractorEmail: contractor.email,
    workOrderNumber: 'WO-' + Date.now(),
    aiSummary: `${aiAnalysis.issueCategory.primary} issue - ${aiAnalysis.priorityAssessment.level} priority. ${aiAnalysis.priorityAssessment.reasoning}`
  }
};

