const chatInput = $input.first().json.chatInput;
const parts = chatInput.split('|').map(part => part.trim());

if (parts.length < 2) {
  throw new Error('Please provide input in format: "document_path_or_url | your_question"\nExamples:\n- "/Users/docs/readme.md | What is this project about?"\n- "https://docs.example.com/api | What are the endpoints?"');
}

const documentPath = parts[0];
const userQuestion = parts.slice(1).join('|').trim();

if (!documentPath || documentPath.length < 3) {
  throw new Error('Please provide a valid document path or URL');
}

if (!userQuestion || userQuestion.length < 5) {
  throw new Error('Please provide a detailed question (minimum 5 characters)');
}

const isUrl = documentPath.startsWith('http://') || documentPath.startsWith('https://');

let fileType = 'unknown';
let inputType = 'file';

if (isUrl) {
  inputType = 'url';
  fileType = 'html';

  const urlParts = documentPath.split('.');
  if (urlParts.length > 1) {
    const possibleExtension = urlParts[urlParts.length - 1].split(/[?#]/)[0].toLowerCase();
    const supportedTypes = ['pdf', 'md', 'txt', 'doc', 'docx', 'json', 'yaml', 'yml'];
    if (supportedTypes.includes(possibleExtension)) {
      fileType = possibleExtension;
    }
  }
} else {
  const fileExtension = documentPath.split('.').pop().toLowerCase();
  const supportedTypes = ['pdf', 'md', 'txt', 'doc', 'docx', 'json', 'yaml', 'yml'];

  if (!supportedTypes.includes(fileExtension)) {
    throw new Error(`Unsupported file type: ${fileExtension}. Supported types: ${supportedTypes.join(', ')}`);
  }

  fileType = fileExtension;
}

return {
  documentPath: documentPath,
  userQuestion: userQuestion,
  fileType: fileType,
  inputType: inputType,
  isUrl: isUrl,
  timestamp: new Date().toISOString()
};

const fileType = $node['Parse Document & Question'].json.fileType;
const inputType = $node['Parse Document & Question'].json.inputType;
const isUrl = $node['Parse Document & Question'].json.isUrl;
const documentPath = $node['Parse Document & Question'].json.documentPath;

let extractedContent;
let processedContent;

const inputData = $input.first();

if (isUrl) {
  extractedContent = inputData.json.body || inputData.json.data || inputData.json || '';
  if (fileType === 'html') {
    const htmlString = typeof extractedContent === 'object' ? JSON.stringify(extractedContent) : String(extractedContent || '');
    processedContent = htmlString
      .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
      .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
      .replace(/<[^>]*>/g, ' ')
      .replace(/&nbsp;/g, ' ')
      .replace(/&amp;/g, '&')
      .replace(/&lt;/g, '<')
      .replace(/&gt;/g, '>')
      .replace(/&quot;/g, '"')
      .replace(/&#39;/g, "'")
      .replace(/\s+/g, ' ')
      .trim();
  } else {
    processedContent = typeof extractedContent === 'object' ? JSON.stringify(extractedContent) : String(extractedContent || '');
  }
} else {
  extractedContent = inputData.json.data || inputData.json.text || inputData.json || inputData.binary?.data?.toString() || '';
  processedContent = typeof extractedContent === 'object' ? JSON.stringify(extractedContent) : String(extractedContent || '');
}
switch(fileType) {
  case 'json':
    try {
      const jsonData = JSON.parse(processedContent);
      processedContent = JSON.stringify(jsonData, null, 2);
    } catch (e) {
      processedContent = processedContent;
    }
    break;

  case 'yaml':
  case 'yml':
    break;

  case 'md':
    processedContent = processedContent
      .replace(/^\s*```[^\n]*\n/gm, '\n--- CODE BLOCK ---\n')
      .replace(/^\s*```\s*$/gm, '\n--- END CODE BLOCK ---\n');
    break;

  case 'html':
    break;

  default:
    processedContent = processedContent
      .replace(/\s+/g, ' ')
      .trim();
}

if (!processedContent || processedContent.length < 10) {
  throw new Error(`Failed to extract meaningful content from ${isUrl ? 'web page' : 'document'}. Content may be empty, corrupted, or unsupported.`);
}

const maxLength = 15000;
const originalLength = processedContent.length;
if (processedContent.length > maxLength) {
  processedContent = processedContent.substring(0, maxLength) + '\n\n[Content truncated for processing...]';
}

return {
  documentContent: processedContent,
  documentPath: documentPath,
  fileType: fileType,
  inputType: inputType,
  isUrl: isUrl,
  contentLength: processedContent.length,
  isContentTruncated: originalLength > maxLength
};
