// Retrieve maintenance history for vehicle
const data = $input.first().json;

const vehicleDatabase = {
  lastInspectionMileage: data.currentMileage - 3500,
  lastOilChangeMileage: data.currentMileage - 2800,
  lastTireRotationMileage: data.currentMileage - 4200,
  lastBrakeInspectionMileage: data.currentMileage - 5100,
  oilChangeIntervalMiles: 5000,
  tireRotationIntervalMiles: 6000,
  brakeInspectionIntervalMiles: 10000,
  annualInspectionDue: '2025-12-15',
  registrationExpiry: '2025-11-30',
  insuranceExpiry: '2026-03-15',
  vehicleAge: new Date().getFullYear() - parseInt(data.vehicleYear),
  fleetCategory: 'delivery',
  dotInspectionDue: '2025-10-20'
};

const oilChangeDue = (data.currentMileage - vehicleDatabase.lastOilChangeMileage) >= (vehicleDatabase.oilChangeIntervalMiles * 0.9);
const tireRotationDue = (data.currentMileage - vehicleDatabase.lastTireRotationMileage) >= (vehicleDatabase.tireRotationIntervalMiles * 0.9);
const brakeInspectionDue = (data.currentMileage - vehicleDatabase.lastBrakeInspectionMileage) >= (vehicleDatabase.brakeInspectionIntervalMiles * 0.9);

const today = new Date();
const annualInspectionOverdue = new Date(vehicleDatabase.annualInspectionDue) < today;
const dotInspectionOverdue = new Date(vehicleDatabase.dotInspectionDue) < today;
const registrationExpiringSoon = (new Date(vehicleDatabase.registrationExpiry) - today) / (1000 * 60 * 60 * 24) < 30;

return {
  json: {
    ...data,
    lastOilChangeMileage: vehicleDatabase.lastOilChangeMileage,
    lastTireRotationMileage: vehicleDatabase.lastTireRotationMileage,
    lastBrakeInspectionMileage: vehicleDatabase.lastBrakeInspectionMileage,
    oilChangeDue: oilChangeDue,
    tireRotationDue: tireRotationDue,
    brakeInspectionDue: brakeInspectionDue,
    milesSinceOilChange: data.currentMileage - vehicleDatabase.lastOilChangeMileage,
    milesSinceTireRotation: data.currentMileage - vehicleDatabase.lastTireRotationMileage,
    milesSinceBrakeInspection: data.currentMileage - vehicleDatabase.lastBrakeInspectionMileage,
    annualInspectionDue: vehicleDatabase.annualInspectionDue,
    annualInspectionOverdue: annualInspectionOverdue,
    dotInspectionDue: vehicleDatabase.dotInspectionDue,
    dotInspectionOverdue: dotInspectionOverdue,
    registrationExpiry: vehicleDatabase.registrationExpiry,
    registrationExpiringSoon: registrationExpiringSoon,
    vehicleAge: vehicleDatabase.vehicleAge,
    fleetCategory: vehicleDatabase.fleetCategory
  }
};

