const inspectionData = $input.first().json;
const aiAnalysisRaw = inspectionData.aiAnalysis;

let aiAnalysis;
try {
  aiAnalysis = JSON.parse(aiAnalysisRaw);
} catch (e) {
  aiAnalysis = {
    overallStatus: {condition: 'good', driveable: true, requiresImmediateAction: false, safetyRating: 85, summary: 'Vehicle in acceptable condition'},
    criticalIssues: [],
    maintenanceRequired: {immediate: [], scheduled: []},
    complianceStatus: {dotCompliant: true, roadworthy: true, complianceIssues: []},
    costAnalysis: {totalEstimatedCost: '$0-500', potentialDowntime: '0 hours'},
    workOrderGeneration: {createWorkOrder: false, workOrderType: 'routine', assignTo: 'general_maintenance', instructions: []}
  };
}

let vehicleStatus = 'operational';
if (!aiAnalysis.overallStatus.driveable) {
  vehicleStatus = 'out_of_service';
} else if (aiAnalysis.overallStatus.requiresImmediateAction) {
  vehicleStatus = 'requires_attention';
}

const workOrderNumber = aiAnalysis.workOrderGeneration.createWorkOrder ? 'WO-' + inspectionData.vehicleId + '-' + Date.now() : null;

return {
  json: {
    ...inspectionData,
    overallCondition: aiAnalysis.overallStatus.condition,
    driveable: aiAnalysis.overallStatus.driveable,
    requiresImmediateAction: aiAnalysis.overallStatus.requiresImmediateAction,
    safetyRating: aiAnalysis.overallStatus.safetyRating,
    statusSummary: aiAnalysis.overallStatus.summary,
    vehicleStatus: vehicleStatus,
    criticalIssues: aiAnalysis.criticalIssues,
    criticalIssueCount: aiAnalysis.criticalIssues.length,
    immediateMaintenanceNeeded: aiAnalysis.maintenanceRequired.immediate,
    scheduledMaintenanceNeeded: aiAnalysis.maintenanceRequired.scheduled,
    dotCompliant: aiAnalysis.complianceStatus.dotCompliant,
    roadworthy: aiAnalysis.complianceStatus.roadworthy,
    complianceIssues: aiAnalysis.complianceStatus.complianceIssues,
    totalEstimatedCost: aiAnalysis.costAnalysis.totalEstimatedCost,
    potentialDowntime: aiAnalysis.costAnalysis.potentialDowntime,
    createWorkOrder: aiAnalysis.workOrderGeneration.createWorkOrder,
    workOrderNumber: workOrderNumber,
    workOrderType: aiAnalysis.workOrderGeneration.workOrderType,
    assignTo: aiAnalysis.workOrderGeneration.assignTo,
    workOrderInstructions: aiAnalysis.workOrderGeneration.instructions
  }
};

