// Process BMKG weather data
const inputData = $input.all();
const weatherData = inputData[0].json;

// Check if data exists
if (!weatherData || !weatherData.data || !weatherData.data[0] || !weatherData.data[0].cuaca) {
    return [{
        json: {
            success: false,
            error: 'Weather data not available from BMKG API',
            timestamp: new Date().toISOString()
        }
    }];
}

// Extract location information
const location = weatherData.lokasi || {};
const cuacaData = weatherData.data[0].cuaca;

// Process current and upcoming weather
let processedData = {
    success: true,
    timestamp: new Date().toISOString(),
    location: {
        desa: location.desa || 'Unknown',
        kecamatan: location.kecamatan || 'Unknown',
        kotkab: location.kotkab || 'Unknown',
        provinsi: location.provinsi || 'Unknown',
        lat: location.lat || 0,
        lon: location.lon || 0,
        timezone: location.timezone || 'Asia/Jakarta'
    },
    current_weather: {},
    forecast_24hour: [],
    summary: {
        avg_temperature: 0,
        avg_humidity: 0,
        dominant_condition: '',
        warnings: []
    }
};

// Process current weather (first data point)
if (cuacaData[0] && cuacaData[0][0]) {
    const current = cuacaData[0][0];
    processedData.current_weather = {
        time: current.local_datetime || current.datetime,
        temperature: current.t || 0,
        humidity: current.hu || 0,
        condition: current.weather_desc || current.weather_desc_en || 'Unknown',
        wind_speed: current.ws || 0,
        wind_direction: current.wd || 'N/A',
        visibility: current.vs_text || 'N/A',
        cloud_cover: current.tcc || 0,
        rain_probability: current.tp || 0,
        icon_url: current.image || ''
    };
}

// Process 24-hour forecast
let totalTemperature = 0;
let totalHumidity = 0;
let count = 0;
let conditionCount = {};

// Collect data from first day (today)
if (cuacaData[0]) {
    cuacaData[0].forEach(jam => {
        if (jam && jam.t !== undefined) {
            processedData.forecast_24hour.push({
                time: jam.local_datetime || jam.datetime,
                temperature: jam.t,
                humidity: jam.hu || 0,
                condition: jam.weather_desc || jam.weather_desc_en || 'Unknown',
                wind_speed: jam.ws || 0,
                wind_direction: jam.wd || 'N/A',
                rain_probability: jam.tp || 0,
                icon_url: jam.image || ''
            });

            totalTemperature += jam.t;
            totalHumidity += jam.hu || 0;
            count++;

            const condition = jam.weather_desc || jam.weather_desc_en || 'Unknown';
            conditionCount[condition] = (conditionCount[condition] || 0) + 1;
        }
    });
}

// Calculate averages and dominant condition
if (count > 0) {
    processedData.summary.avg_temperature = Math.round(totalTemperature / count * 10) / 10;
    processedData.summary.avg_humidity = Math.round(totalHumidity / count);
}

// Find dominant weather condition
let maxCount = 0;
for (const [condition, conditionNum] of Object.entries(conditionCount)) {
    if (conditionNum > maxCount) {
        maxCount = conditionNum;
        processedData.summary.dominant_condition = condition;
    }
}

// Generate warnings
const warnings = [];
if (processedData.summary.avg_temperature > 35) {
    warnings.push('‚ö†Ô∏è Very hot temperature, avoid outdoor activities');
}
if (processedData.summary.avg_temperature < 20) {
    warnings.push('üß• Cold temperature, wear warm clothes');
}

// Check for rain probability
const avgRainProb = processedData.forecast_24hour.reduce((sum, item) => sum + (item.rain_probability || 0), 0) / processedData.forecast_24hour.length;
if (avgRainProb > 60) {
    warnings.push('‚òî High rain probability, bring umbrella or raincoat');
}
if (avgRainProb > 30 && avgRainProb <= 60) {
    warnings.push('üå¶Ô∏è Possible rain, prepare rain gear');
}

processedData.summary.warnings = warnings;

return [{ json: processedData }];

// Format weather data for Telegram message
const data = $input.all()[0].json;

if (!data.success) {
    return [{
        json: {
            telegram_message: `‚ùå *BMKG Weather Monitoring Error*\n\n${data.error}\n\nTime: ${data.timestamp}\n\n_Powered by BMKG API & n8n_`,
            parse_mode: 'Markdown'
        }
    }];
}

// Create beautiful Telegram message
let message = `üå§Ô∏è *BMKG Weather Report*\n`;
message += `üìç *${data.location.desa}, ${data.location.kecamatan}*\n`;
message += `üèôÔ∏è ${data.location.kotkab}, ${data.location.provinsi}\n\n`;

// Current weather
message += `üå°Ô∏è *Current Weather*\n`;
message += `‚Ä¢ Temperature: *${data.current_weather.temperature}¬∞C*\n`;
message += `‚Ä¢ Condition: ${data.current_weather.condition}\n`;
message += `‚Ä¢ Humidity: ${data.current_weather.humidity}%\n`;
message += `‚Ä¢ Wind: ${data.current_weather.wind_speed} km/h from ${data.current_weather.wind_direction}\n`;
message += `‚Ä¢ Visibility: ${data.current_weather.visibility}\n`;

if (data.current_weather.rain_probability > 0) {
    message += `‚Ä¢ Rain Prob.: ${data.current_weather.rain_probability}%\n`;
}
message += `\n`;

// Summary
message += `üìä *24 Hour Summary*\n`;
message += `‚Ä¢ Average Temperature: *${data.summary.avg_temperature}¬∞C*\n`;
message += `‚Ä¢ Average Humidity: ${data.summary.avg_humidity}%\n`;
message += `‚Ä¢ Dominant Condition: ${data.summary.dominant_condition}\n\n`;

// Warnings
if (data.summary.warnings.length > 0) {
    message += `‚ö†Ô∏è *Warnings & Suggestions:*\n`;
    data.summary.warnings.forEach(warning => {
        message += `‚Ä¢ ${warning}\n`;
    });
    message += `\n`;
}

// Next few hours forecast (first 6 hours)
message += `‚è∞ *6 Hour Forecast:*\n`;
const nextHours = data.forecast_24hour.slice(0, 6);
nextHours.forEach((forecast, index) => {
    const time = new Date(forecast.time).toLocaleTimeString('en-US', { 
        hour: '2-digit', 
        minute: '2-digit',
        timeZone: data.location.timezone 
    });
    message += `‚Ä¢ ${time}: ${forecast.temperature}¬∞C, ${forecast.condition}`;
    if (forecast.rain_probability > 30) {
        message += ` (‚òî${forecast.rain_probability}%)`;
    }
    message += `\n`;
});

message += `\nüìÖ Updated: ${new Date(data.timestamp).toLocaleString('en-US', {
    timeZone: data.location.timezone,
    year: 'numeric',
    month: 'long',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit'
})} ${data.location.timezone}`;

message += `\n\n_ü§ñ Automated by n8n | üì° Data: BMKG_`;

return [{
    json: {
        telegram_message: message,
        parse_mode: 'Markdown',
        weather_data: data
    }
}];

// Log error details
const inputData = $input.all()[0].json;

console.error('Weather monitoring error:', inputData);

let errorMessage = `‚ùå *Weather Monitoring System Error*\n\n`;
errorMessage += `üïê Time: ${new Date().toLocaleString('en-US')}\n`;
errorMessage += `‚ùó Details: ${inputData.error || 'Unknown error'}\n\n`;
errorMessage += `_System will try again on next schedule_`;

return [{
    json: {
        telegram_message: errorMessage,
        parse_mode: 'Markdown',
        error_logged: true
    }
}];
