const emailData = items[0].json;

const extractedData = {
  sender: emailData.from || 'Unknown Sender',
  subject: emailData.subject || 'No Subject',
  bodyText: emailData.textPlain || emailData.text || '',
  bodyHtml: emailData.html || '',
  attachments: emailData.attachments || [],
  messageId: emailData.id,
  receivedDate: emailData.date || new Date().toISOString(),
  threadId: emailData.threadId
};

const linkRegex = /https?:\/\/[^\s<>"{}|\\^`\[\]]+/gi;
const textLinks = (extractedData.bodyText.match(linkRegex) || []);
const htmlLinks = (extractedData.bodyHtml.match(linkRegex) || []);
const allLinks = [...new Set([...textLinks, ...htmlLinks])];

extractedData.links = allLinks;

// ENHANCED ATTACHMENT DETECTION LOGIC
let attachmentCount = 0;
let hasAttachments = false;

// Check the original attachments array first
if (extractedData.attachments && extractedData.attachments.length > 0) {
  attachmentCount = extractedData.attachments.length;
  hasAttachments = true;
}

// Check Gmail payload parts for attachments
if (!hasAttachments && emailData.payload && emailData.payload.parts) {
  const attachmentParts = emailData.payload.parts.filter(part => 
    part.filename && part.filename !== '' && part.body && part.body.attachmentId
  );
  if (attachmentParts.length > 0) {
    attachmentCount = attachmentParts.length;
    hasAttachments = true;
  }
}

// Check content-type header for multipart/mixed (indicates attachments)
if (!hasAttachments && emailData.headers && emailData.headers['content-type']) {
  const contentType = emailData.headers['content-type'];
  if (contentType.includes('multipart/mixed')) {
    hasAttachments = true;
    attachmentCount = 1; // At least one attachment
  }
}

// Check email content for attachment keywords as final fallback
if (!hasAttachments) {
  const emailText = (extractedData.bodyText + ' ' + extractedData.bodyHtml).toLowerCase();
  const attachmentKeywords = ['attached', 'attachment', 'please review the attached', 'pdf', 'excel', 'document'];

  if (attachmentKeywords.some(keyword => emailText.includes(keyword))) {
    hasAttachments = true;
    attachmentCount = 1;
  }
}

extractedData.hasAttachments = hasAttachments;
extractedData.attachmentCount = attachmentCount;

const bodyForAnalysis = extractedData.bodyText || extractedData.bodyHtml.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();
extractedData.bodyForAnalysis = bodyForAnalysis.substring(0, 3000);

return [{ json: extractedData }];

const emailData = items[0].json;
let attachmentText = '';
const processedAttachments = [];

if (emailData.attachments && emailData.attachments.length > 0) {
  for (const attachment of emailData.attachments) {
    const processed = {
      filename: attachment.filename || 'unknown',
      contentType: attachment.contentType || 'unknown',
      size: attachment.size || 0,
      textExtracted: false,
      extractedText: ''
    };

    if (attachment.contentType) {
      if (attachment.contentType.includes('text/')) {
        processed.extractedText = 'Text file content available';
        processed.textExtracted = true;
      } else if (attachment.contentType.includes('pdf')) {
        processed.extractedText = 'PDF attachment detected - text extraction requires additional service';
        processed.textExtracted = false;
      } else if (attachment.contentType.includes('word') || attachment.contentType.includes('document')) {
        processed.extractedText = 'Word document detected - text extraction requires additional service';
        processed.textExtracted = false;
      }
    }

    processedAttachments.push(processed);

    if (processed.textExtracted) {
      attachmentText += `\n[Attachment: ${processed.filename}] ${processed.extractedText}`;
    }
  }
}

const combinedContent = emailData.bodyForAnalysis + attachmentText;

return [{
  json: {
    ...emailData,
    processedAttachments,
    combinedContent: combinedContent.substring(0, 4000),
    attachmentProcessingComplete: true
  }
}];

const emailData = items[0].json;

return [{
  json: {
    ...emailData,
    processedAttachments: [],
    combinedContent: emailData.bodyForAnalysis,
    attachmentProcessingComplete: true
  }
}];

// Parse AI response and handle the actual output structure
let aiData;
try {
  // Get the AI response - handle multiple possible structures
  let aiResponse;

  // Check if it's the direct output structure you showed
  if (items[0].json.output) {
    aiResponse = items[0].json.output;
  }
  // Check for standard OpenAI response formats
  else if (items[0].json.message?.content) {
    const content = items[0].json.message.content;
    // Try to parse JSON from content
    const jsonMatch = content.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      aiResponse = JSON.parse(jsonMatch[0]);
    } else {
      aiResponse = content;
    }
  }
  // Check for other possible response formats
  else if (items[0].json.text) {
    const text = items[0].json.text;
    const jsonMatch = text.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      aiResponse = JSON.parse(jsonMatch[0]);
    } else {
      aiResponse = text;
    }
  }
  // Check if the response is directly in the json
  else if (items[0].json.summary || items[0].json.callToAction || items[0].json.insights) {
    aiResponse = items[0].json;
  }
  else {
    throw new Error('Unknown AI response format');
  }

  // Now handle the parsed response
  if (typeof aiResponse === 'object' && aiResponse.summary) {
    // Direct object format (like your output)
    aiData = {
      summary: aiResponse.summary || 'AI analysis unavailable',
      callToAction: aiResponse.callToAction || 'For Your Information',
      insights: aiResponse.insights || 'No specific insights available'
    };
  } else if (typeof aiResponse === 'string') {
    // String format - try to parse JSON
    const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      const parsed = JSON.parse(jsonMatch[0]);
      aiData = {
        summary: parsed.summary || 'AI analysis unavailable',
        callToAction: parsed.callToAction || 'For Your Information',
        insights: parsed.insights || 'No specific insights available'
      };
    } else {
      throw new Error('Could not extract JSON from AI response');
    }
  } else {
    throw new Error('Unexpected AI response format');
  }

  // Validate and clean up the data
  if (!aiData.summary) aiData.summary = 'AI analysis unavailable';
  if (!aiData.callToAction) aiData.callToAction = 'For Your Information';
  if (!aiData.insights) aiData.insights = 'No specific insights available';

  // Ensure callToAction is one of the expected values
  const validActions = ['Reply Needed', 'Review Attachment', 'For Your Information'];
  if (!validActions.includes(aiData.callToAction)) {
    aiData.callToAction = 'For Your Information';
  }

} catch (error) {
  // Fallback if AI parsing fails
  aiData = {
    summary: 'Email received but AI analysis failed. Please review manually.',
    callToAction: 'For Your Information',
    insights: 'Manual review required due to AI processing error.',
    aiError: true,
    aiErrorMessage: error.message
  };
}

