// Format only essential data for Google Sheets logging
const slackResponse = items[0].json;
const slackMessage = slackResponse.message || {};
const messageText = slackMessage.text || '';

// Extract key information from Slack message
const fromMatch = messageText.match(/\*From:\*\s*(?:<mailto:([^|>]+)\|([^>]+)>|([^\n*]+))/);
const subjectMatch = messageText.match(/\*Subject:\*\s*([^\n*]+)/);
const summaryMatch = messageText.match(/\*AI Summary:\*\s*([\s\S]*?)(?=\n\n\*|\n\*|$)/);
const actionMatch = messageText.match(/\*Action Required:\*\s*([^\n*]+)/);

// Determine channel based on message content
let channelName = '#general';
if (messageText.includes('URGENT EMAIL REQUIRES REPLY')) {
  channelName = '#urgent';
} else if (messageText.includes('EMAIL WITH ATTACHMENTS')) {
  channelName = '#attachments';
}

// Check for attachments
const hasAttachments = messageText.includes('üìé') || messageText.includes('Attachments');

// Essential data only (6-7 columns)
const essentialData = {
  timestamp: new Date().toISOString(),
  sender: fromMatch?.[1] || fromMatch?.[2] || fromMatch?.[3] || 'Unknown',
  subject: subjectMatch?.[1]?.trim() || 'Unknown',
  ai_summary: summaryMatch?.[1]?.trim().replace(/\n/g, ' ').substring(0, 200) || 'No summary',
  action_required: actionMatch?.[1]?.trim() || 'For Your Information',
  slack_channel: channelName,
  has_attachments: hasAttachments
};

return [{
  json: essentialData
}];
