parameters.jsCode:
const offboardingData = $input.first().json;
const aiAnalysisRaw = offboardingData.aiAnalysis;

let aiAnalysis;
try {
  aiAnalysis = JSON.parse(aiAnalysisRaw);
} catch (e) {
  aiAnalysis = {
    retentionAnalysis: {flightRisk: 'voluntary', primaryReason: 'personal', preventable: false, preventabilityScore: 30, reasoning: 'Personal reasons'},
    sentimentAnalysis: {overallSentiment: 'neutral', sentimentScore: 50, keyEmotions: ['professional'], toneDescription: 'Professional'},
    managerPerformance: {managerIssue: false, managementConcerns: [], managerActionRequired: false, managerCoaching: 'None'},
    departmentInsights: {departmentIssues: [], culturalConcerns: [], processImprovements: [], teamImpact: 'Minimal'},
    compensationInsights: {compensationIssue: false, marketCompetitiveness: 'at', benefitsAdequate: true, recommendedAdjustment: 'None'},
    retentionRecommendations: [],
    boomerangPotential: {likelyToReturn: true, returnProbability: 50, rehireRecommendation: 'recommend', rehireNotes: 'Good performer'},
    knowledgeTransferRisk: {criticalKnowledgeRisk: 'medium', documentationStatus: 'good', transferComplexity: 'moderate', transferPlan: ['Document processes']},
    competitorIntelligence: {competitorMoving: false, competitorName: 'Unknown', competitorAdvantages: [], competitiveThreat: 'low'},
    redFlags: {hasRedFlags: false, flaggedIssues: [], requiresInvestigation: false, urgency: 'low', escalationRequired: false},
    actionItems: [],
    executiveSummary: 'Standard voluntary departure.',
    hrPriority: 3
  };
}

const lastDay = new Date(offboardingData.lastWorkingDay);
const today = new Date();
const daysUntilLastDay = Math.ceil((lastDay - today) / (1000 * 60 * 60 * 24));

const equipmentList = [];
if (offboardingData.hasLaptop === 'yes') equipmentList.push('Laptop: ' + offboardingData.laptopSerialNumber);
if (offboardingData.hasMobile === 'yes') equipmentList.push('Mobile: ' + offboardingData.mobileNumber);
if (offboardingData.hasKeyCard === 'yes') equipmentList.push('Key Card');
if (offboardingData.hasCreditCard === 'yes') equipmentList.push('Corporate Card');
if (offboardingData.otherEquipment) equipmentList.push('Other: ' + offboardingData.otherEquipment);

return {
  json: {
    ...offboardingData,
    daysUntilLastDay: daysUntilLastDay,
    equipmentList: equipmentList,
    equipmentCount: equipmentList.length,
    flightRisk: aiAnalysis.retentionAnalysis.flightRisk,
    primaryReason: aiAnalysis.retentionAnalysis.primaryReason,
    preventable: aiAnalysis.retentionAnalysis.preventable,
    preventabilityScore: aiAnalysis.retentionAnalysis.preventabilityScore,
    retentionReasoning: aiAnalysis.retentionAnalysis.reasoning,
    overallSentiment: aiAnalysis.sentimentAnalysis.overallSentiment,
    sentimentScore: aiAnalysis.sentimentAnalysis.sentimentScore,
    keyEmotions: aiAnalysis.sentimentAnalysis.keyEmotions,
    toneDescription: aiAnalysis.sentimentAnalysis.toneDescription,
    managerIssue: aiAnalysis.managerPerformance.managerIssue,
    managementConcerns: aiAnalysis.managerPerformance.managementConcerns,
    managerActionRequired: aiAnalysis.managerPerformance.managerActionRequired,
    managerCoaching: aiAnalysis.managerPerformance.managerCoaching,
    departmentIssues: aiAnalysis.departmentInsights.departmentIssues,
    culturalConcerns: aiAnalysis.departmentInsights.culturalConcerns,
    processImprovements: aiAnalysis.departmentInsights.processImprovements,
    teamImpact: aiAnalysis.departmentInsights.teamImpact,
    compensationIssue: aiAnalysis.compensationInsights.compensationIssue,
    marketCompetitiveness: aiAnalysis.compensationInsights.marketCompetitiveness,
    benefitsAdequate: aiAnalysis.compensationInsights.benefitsAdequate,
    salaryRecommendation: aiAnalysis.compensationInsights.recommendedAdjustment,
    retentionRecommendations: aiAnalysis.retentionRecommendations,
    likelyToReturn: aiAnalysis.boomerangPotential.likelyToReturn,
    returnProbability: aiAnalysis.boomerangPotential.returnProbability,
    rehireRecommendation: aiAnalysis.boomerangPotential.rehireRecommendation,
    rehireNotes: aiAnalysis.boomerangPotential.rehireNotes,
    knowledgeRisk: aiAnalysis.knowledgeTransferRisk.criticalKnowledgeRisk,
    documentationStatus: aiAnalysis.knowledgeTransferRisk.documentationStatus,
    transferComplexity: aiAnalysis.knowledgeTransferRisk.transferComplexity,
    transferPlan: aiAnalysis.knowledgeTransferRisk.transferPlan,
    movingToCompetitor: aiAnalysis.competitorIntelligence.competitorMoving,
    competitorName: aiAnalysis.competitorIntelligence.competitorName,
    competitorAdvantages: aiAnalysis.competitorIntelligence.competitorAdvantages,
    competitiveThreat: aiAnalysis.competitorIntelligence.competitiveThreat,
    hasRedFlags: aiAnalysis.redFlags.hasRedFlags,
    flaggedIssues: aiAnalysis.redFlags.flaggedIssues,
    requiresInvestigation: aiAnalysis.redFlags.requiresInvestigation,
    flagUrgency: aiAnalysis.redFlags.urgency,
    escalationRequired: aiAnalysis.redFlags.escalationRequired,
    actionItems: aiAnalysis.actionItems,
    executiveSummary: aiAnalysis.executiveSummary,
    hrPriority: aiAnalysis.hrPriority
  }
};

