// Sort by part number if available
items.sort((a, b) => (a.json.part || 0) - (b.json.part || 0));

// Join transcript texts with newlines
const fullText = items.map(item => item.json.text).join("\n");
const date = $('[Step 4] Item ‚Äî Select Enclosure URL & Date').first().json.date;

return [{ json: { fullText, date } }];

const url = $('[Step 4] Item ‚Äî Select Enclosure URL & Date').first().json.enclosure.url; // ‚Üê Always present
const cr  = $json.headers?.['content-range'];

if (!cr) throw new Error('No content-range header found. Check if "Return Full Response" is ON in the HTTP(0-0) node or if the status is 206.');

const total = Number(cr.split('/')[1]);
const CHUNK = 24 * 1024 * 1024; // 24MB chunks
const out = [];
let start = 0, part = 1;

while (start < total) {
  const end = Math.min(start + CHUNK - 1, total - 1);
  out.push({ json: { url, part, start, end } });
  start = end + 1; part++;
}
return out;

// Get the date string from the RSS node
const dateStr = $('[Step 1] RSS ‚Äî Fetch Feed').first().json.pubDate; 

// Convert to Date object
const date = new Date(dateStr);

// Get year, month, and day
const year = date.getFullYear();
const month = date.getMonth() + 1; // Month is 0-indexed
const day = date.getDate();

// Return formatted string (YYYY-MM-DD)
return [
  {    json: {
      original: dateStr,
      formatted: `${year}-${month.toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}`
    }
  }
];
