dataFields.fieldValue:
={{ 
  // Access the full name string from the JSON input
  $json.billing_details.name

  // Remove any leading/trailing whitespace
  ?.trim()

  // Split the name string into an array using one or more whitespace characters (handles multiple spaces)
  .split(/\s+/)

  // Select the first word from the array (typically the first name)
  [0] 
}}

