const items = $input.all();

return items.map(item => {
  const birthDate = new Date(item.json.Date_of_Birth);
  const ageInMs = Date.now() - birthDate;
  const ageMonths = Math.floor(ageInMs / (1000 * 60 * 60 * 24 * 30.44));
  const ageYears = Math.floor(ageMonths / 12);
  const remainingMonths = ageMonths % 12;

  let petAge;
  if (ageMonths < 12) {
    petAge = `${ageMonths} month${ageMonths !== 1 ? 's' : ''}`;
  } else if (remainingMonths === 0) {
    petAge = `${ageYears} year${ageYears !== 1 ? 's' : ''}`;
  } else {
    petAge = `${ageYears} year${ageYears !== 1 ? 's' : ''} and ${remainingMonths} month${remainingMonths !== 1 ? 's' : ''}`;
  }

  return {
    json: {
      ...item.json,
      pet_age_months: ageMonths,
      pet_age: petAge
    }
  };
});

const items = $input.all();

return items.filter(item => {
  // Must be Active status
  if (item.json.Status !== 'Active') return false;

  // Never sent email = include
  if (!item.json.Last_Email_Sent) return true;

  // Sent >7 days ago = include
  const daysSince = (Date.now() - new Date(item.json.Last_Email_Sent)) / (1000*60*60*24);
  return daysSince > 7;
});

// Get pet data from Calculate Pet Age node
const petData = $('Calculate Pet Age').item.json;
const aiResponse = $json.message.content;

const cleanResponse = aiResponse.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

let tipData;
try {
  tipData = JSON.parse(cleanResponse);
} catch (e) {
  tipData = {
    topic: 'Weekly Pet Care Tip',
    headline: `Important advice for ${petData.Pet_Name}`,
    content: cleanResponse,
    category: 'General'
  };
}

return {
  json: {
    email: petData.Email,
    owner_name: petData.Owner_Name,
    pet_name: petData.Pet_Name,
    pet_type: petData.Pet_Type,
    pet_age: petData.pet_age,
    pet_age_months: petData.pet_age_months,
    tip_topic: tipData.topic,
    tip_headline: tipData.headline,
    tip_content: tipData.content,
    tip_category: tipData.category,
    row_number: petData.row_number
  }
};
