const data = $input.first().json;

// Training budget database per department (annual)
const departmentBudgets = {
  'Engineering': { total: 50000, spent: 28000, remaining: 22000 },
  'Sales': { total: 30000, spent: 18000, remaining: 12000 },
  'Marketing': { total: 25000, spent: 15000, remaining: 10000 },
  'HR': { total: 15000, spent: 8000, remaining: 7000 },
  'Finance': { total: 20000, spent: 12000, remaining: 8000 },
  'Operations': { total: 18000, spent: 10000, remaining: 8000 },
  'Customer Support': { total: 12000, spent: 6000, remaining: 6000 },
  'Product': { total: 35000, spent: 20000, remaining: 15000 }
};

// Training catalog with costs
const trainingCatalog = {
  'Technical': [
    { name: 'Python Programming Fundamentals', duration: '3 days', cost: 1200, level: 'Beginner', provider: 'TechAcademy' },
    { name: 'Advanced JavaScript & React', duration: '5 days', cost: 2000, level: 'Intermediate', provider: 'CodeSchool' },
    { name: 'AWS Cloud Practitioner', duration: '2 days', cost: 800, level: 'Beginner', provider: 'CloudExperts' },
    { name: 'Docker & Kubernetes', duration: '4 days', cost: 1800, level: 'Advanced', provider: 'DevOpsInstitute' },
    { name: 'Cybersecurity Essentials', duration: '3 days', cost: 1500, level: 'Intermediate', provider: 'SecureLearn' }
  ],
  'Leadership': [
    { name: 'Leadership Fundamentals', duration: '2 days', cost: 1000, level: 'Beginner', provider: 'LeadershipPro' },
    { name: 'Strategic Management', duration: '3 days', cost: 1800, level: 'Advanced', provider: 'ExecutiveEd' },
    { name: 'Team Building & Motivation', duration: '1 day', cost: 600, level: 'Intermediate', provider: 'TeamSuccess' }
  ],
  'Sales': [
    { name: 'Consultative Selling', duration: '2 days', cost: 900, level: 'Intermediate', provider: 'SalesForce Academy' },
    { name: 'Negotiation Mastery', duration: '2 days', cost: 1100, level: 'Advanced', provider: 'DealMakers' },
    { name: 'Sales Fundamentals', duration: '1 day', cost: 500, level: 'Beginner', provider: 'SalesPro' }
  ],
  'Communication': [
    { name: 'Effective Communication', duration: '1 day', cost: 400, level: 'Beginner', provider: 'CommSkills' },
    { name: 'Public Speaking Mastery', duration: '2 days', cost: 800, level: 'Intermediate', provider: 'SpeakWell' },
    { name: 'Presentation Skills', duration: '1 day', cost: 500, level: 'Intermediate', provider: 'PresenterPro' }
  ],
  'Project Management': [
    { name: 'PMP Certification Prep', duration: '5 days', cost: 2500, level: 'Advanced', provider: 'PMI Institute' },
    { name: 'Agile & Scrum Fundamentals', duration: '2 days', cost: 900, level: 'Beginner', provider: 'AgileExperts' },
    { name: 'Project Planning & Execution', duration: '3 days', cost: 1200, level: 'Intermediate', provider: 'ProjectSuccess' }
  ],
  'Data & Analytics': [
    { name: 'Data Analysis with Excel', duration: '2 days', cost: 700, level: 'Beginner', provider: 'DataSkills' },
    { name: 'Power BI Advanced', duration: '3 days', cost: 1400, level: 'Advanced', provider: 'BIExperts' },
    { name: 'SQL for Business', duration: '2 days', cost: 900, level: 'Intermediate', provider: 'DataAcademy' }
  ]
};

const department = data.department;
const budget = departmentBudgets[department] || { total: 10000, spent: 5000, remaining: 5000 };
const estimatedCost = data.estimatedBudget || 1000;

const budgetAvailable = budget.remaining >= estimatedCost;
const budgetUtilization = ((budget.spent / budget.total) * 100).toFixed(1);

return {
  json: {
    ...data,
    departmentBudgetTotal: budget.total,
    departmentBudgetSpent: budget.spent,
    departmentBudgetRemaining: budget.remaining,
    budgetAvailable: budgetAvailable,
    budgetUtilization: budgetUtilization,
    trainingCatalog: trainingCatalog
  }
};

