const items = $input.all();

// Regex para validar subdominios (sin asterisco al inicio)
const validRegex = /^(?!\*)(?:[a-zA-Z0-9-]+\.)+[a-zA-Z]{2,}$/;

// Extrae y filtra los subdominios desde el input din√°mico
const allSubdomains = items
  .flatMap(item => {
    const data = item.json.subdomains || item.json.subdomain || item.json.domain || "";
    return data
      .toString()
      .split(/\s*,\s*|\s+/)
      .map(str => str.trim().replace(/:\d+$/, ""));
  })
  .filter(str => validRegex.test(str));

// Genera la salida como texto con saltos de l√≠nea
const txtOutput = allSubdomains.join("\n");

return [{ json: { subdomain: txtOutput } }];

