const items = $input.all();

// Regex para validar subdominios (sin asterisco al inicio)
const validRegex = /^(?!\*)(?:[a-zA-Z0-9-]+\.)+[a-zA-Z]{2,}$/;

// Extrae y filtra los subdominios desde el input din√°mico
const allSubdomains = items
  .map(item => item.json["message.content.subdomains"] || "")
  .flatMap(data =>
    data
      .toString()
      .split(/\s*,\s*|\s+/)
      .map(str => str.trim().replace(/:\d+$/, ""))
  )
  .filter(str => validRegex.test(str));

// Elimina duplicados
const uniqueSubdomains = [...new Set(allSubdomains)];

// Genera la salida como texto con saltos de l√≠nea
const txtOutput = uniqueSubdomains.join("\n");

return [{ json: { subdomain: txtOutput } }];

let allSubdomains = [];

for (const item of items) {
  const values = Object.values(item.json).flat();

  for (let value of values) {
    if (typeof value === 'string') {
      const parts = value
        .split(/[\n,]+/)
        .map(v => v.trim())
        .filter(v => v);
      allSubdomains.push(...parts);
    }
  }
}

const unique = [...new Set(allSubdomains)];

return [
  {
    json: {
      subdomains: unique
    }
  }
];

