const input = $input.first().json["Passive Results"];

const lines = input.split("\n").filter(line => line.trim() !== "");

const results = lines
  .map(line => {
    const match = line.match(/https:\/\/([a-zA-Z0-9.-]+)/);
    if (match) {
      return { content: line.trim() };
    }
    return null;
  })
  .filter(Boolean)
  .slice(0, 400);

return results.map(r => ({ json: r }));

let combined = {
  "Passive Results": [],
  "AI Results": []
};

for (const item of items) {
  const data = item.json;

  if (data["Passive Results"] && data["Passive Results"].trim() !== "") {
    combined["Passive Results"] = data["Passive Results"]
      .split("\n")
      .map(line => line.trim())
      .filter(line => line !== "");
  }

  if (data["AI Results"] && data["AI Results"].trim() !== "") {
    combined["AI Results"] = data["AI Results"]
      .split("\n")
      .map(line => line.trim())
      .filter(line => line !== "");
  }
}

return [{ json: combined }];
