// ‚îÄ‚îÄ Pull the fields we need from the incoming item ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
const channelId       = $json["youtubeChannels"];
const authorityScore  = $json["authority_score"];
const nicheKeywords   = $json["niche_keywords"];

console.log(
  `Channel: ${channelId}, authority: ${authorityScore}, ` +
  `keywords: ${nicheKeywords?.length || 0}`
);

// ‚îÄ‚îÄ Skip if the channel ID is missing or blank ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
if (!channelId || channelId.trim() === '') {
  return null;
}

// ‚îÄ‚îÄ Only keep well-formed YouTube channel IDs (start with ‚ÄúUC‚Äù, 24 chars) ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
if (channelId.startsWith('UC') && channelId.length === 24) {
  return {
    channelId,
    rssUrl: `https://www.youtube.com/feeds/videos.xml?channel_id=${channelId}`,
    authority_score: authorityScore,
    niche_keywords: nicheKeywords,
    success: true
  };
} else {
  console.log(`‚ùå Invalid Channel ID format: ${channelId}`);
  return null;  // Skip invalid IDs
}

// YouTube Video ID Extractor - returns `video_id`
function extractYouTubeVideoId(url) {
  if (!url || typeof url !== 'string') {
    return null;
  }
  const regex = /(?:youtube\.com\/(?:[^\/\n\s]+\/\S+\/|(?:v|e(?:mbed)?|shorts)\/|.*[?&]v=)|youtu\.be\/)([a-zA-Z0-9_-]{11})/i;
  const match = url.match(regex);
  return match && match[1] ? match[1] : null;
}

const input = $input.all();
const results = [];

for (const item of input) {
  const url =
    item.json.url ||
    item.json.video_url ||
    item.json.youtube_url ||
    (typeof item.json === 'string' ? item.json : '');
  const id = extractYouTubeVideoId(url);
  results.push({
    original_url: url,
    video_id: id,
    is_valid: id !== null,
    clean_url: id ? `https://www.youtube.com/watch?v=${id}` : null,
  });
}

return results;

