const trackToText = (track) => track.transcript.map(t => t.text).join(' ');

const results = [];

for (const cur of $input.all()) {
  // Parse data field if it‚Äôs a string
  let dataArray;
  if (typeof cur.json.data === 'string') {
    try {
      dataArray = JSON.parse(cur.json.data);
    } catch {
      continue;
    }
  } else {
    dataArray = cur.json.data;
  }

  const item = Array.isArray(dataArray) ? dataArray[0] : null;
  if (!item || !item.tracks?.[0]) continue;

  results.push({
    json: {
      id: item.id,
      transcript: trackToText(item.tracks[0])
    }
  });
}

return results;
