// Process 5-day stock data and predict trends
const stockData = $input.first().json;
let processedStocks = [];

// Check for API error
if (stockData.error) {
  throw new Error(`Twelve Data API Error: ${stockData.error.message}`);
}

// Handle multiple stock responses (array of stock objects)
const quotes = Array.isArray(stockData) ? stockData : [stockData];

for (let stock of quotes) {
  if (stock && stock.meta && stock.values && Array.isArray(stock.values)) {
    const symbol = stock.meta.symbol;
    const currency = stock.meta.currency || stock.meta.currency_quote || 'USD';
    const prices = stock.values.map(v => parseFloat(v.close)).filter(p => !isNaN(p));

    // Calculate 5-day trend (simple moving average slope)
    const trendSlope = prices.length >= 2 ? (prices[0] - prices[prices.length - 1]) / prices.length : 0;
    const avgPrice = prices.length > 0 ? prices.reduce((sum, p) => sum + p, 0) / prices.length : 0;
    const predictedChange = trendSlope * 5; // Extrapolate for next 5 days
    const prediction = predictedChange > 0 ? 'Up' : predictedChange < 0 ? 'Down' : 'Neutral';
    const absPredictedChange = Math.abs(predictedChange);

    processedStocks.push({
      symbol: symbol,
      name: stock.meta.currency_base || stock.meta.symbol,
      currency: currency,
      five_day_data: stock.values.map(v => ({
        date: v.datetime,
        close: parseFloat(v.close) || null,
        high: parseFloat(v.high) || null,
        low: parseFloat(v.low) || null,
        volume: parseInt(v.volume) || null
      })),
      avg_price: avgPrice.toFixed(2),
      predicted_trend: prediction,
      predicted_change: absPredictedChange.toFixed(2),
      percent_change: prices.length >= 2 ? ((prices[0] - prices[prices.length - 1]) / prices[prices.length - 1] * 100).toFixed(2) : '0.00'
    });
  }
}

const currentDate = new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' });

return [{
  json: {
    date: currentDate,
    total_stocks: processedStocks.length,
    stock_analysis: processedStocks
  }
}];

// Generate HTML Email content
const data = $input.first().json;
let htmlContent = `<!DOCTYPE html><html><head><style>body { font-family: Arial, sans-serif; margin: 20px; background-color: #f5f5f5; }.container { max-width: 600px; margin: 0 auto; background: white; padding: 20px; border-radius: 10px; }.header { text-align: center; border-bottom: 2px solid #007bff; padding-bottom: 10px; }.section { margin-bottom: 20px; }.stock-table { width: 100%; border-collapse: collapse; }.stock-table th, .stock-table td { padding: 10px; text-align: left; border-bottom: 1px solid #ddd; }.stock-table th { background-color: #f8f9fa; }.gain { color: #28a745; }.loss { color: #dc3545; }.symbol { font-weight: bold; color: #007bff; }</style></head><body><div class="container"><div class="header"><h2>üìà Stock Trend Analysis Report</h2><p><strong>Date:</strong> ${data.date}</p></div><div class="section"><h3>Stock Analysis (Last 5 Days & Predictions)</h3><table class="stock-table"><thead><tr><th>Symbol</th><th>Avg Price</th><th>5-Day % Change</th><th>Predicted Trend</th><th>Predicted Change ($)</th></tr></thead><tbody>`;
data.stock_analysis.forEach(stock => {
  htmlContent += `<tr><td class="symbol">${stock.symbol}</td><td>$${stock.avg_price}</td><td class="${stock.percent_change > 0 ? 'gain' : 'loss'}">${stock.percent_change}%</td><td>${stock.predicted_trend}</td><td>$${stock.predicted_change}</td></tr>`;
});
htmlContent += `</tbody></table></div><div style="text-align: center; margin-top: 20px; color: #666;"><p>‚ö° Generated by Stock Trend Predictor</p><p><small>Data provided by Twelve Data API</small></p></div></body></html>`;
const plainText = `STOCK TREND ANALYSIS REPORT\nDate: ${data.date}\nStocks Analyzed: ${data.total_stocks}\n\nSTOCK ANALYSIS:\n${data.stock_analysis.map((stock, i) => `${i+1}. ${stock.symbol}: Avg $${stock.avg_price}, 5-Day Change: ${stock.percent_change}%, Predicted Trend: ${stock.predicted_trend} ($${stock.predicted_change})`).join('\n')}\n\nGenerated by Stock Trend Predictor`;
return [{ json: { email_html: htmlContent, email_text: plainText, email_subject: `Stock Trend Report - ${data.date}` } }];
