// Enhanced NLP Parser for Hotel Requests
const input = $input.first().json.body;
const query = input.message || input.query || input.text || '';
const userEmail = input.email || input.user_email || '';
const userName = input.name || input.user_name || 'Guest';

// Greeting handler
const greetings = ['hi', 'hello', 'hey', 'start', 'help'];
if (greetings.some(g => query.toLowerCase().includes(g)) && query.split(' ').length < 5) {
  return [{
    json: {
      status: 'greeting',
      response: `Hi ${userName}! üëã I'm your Hotel Price Assistant.\n\nJust tell me:\n‚úÖ Hotel name\n‚úÖ City\n‚úÖ Check-in & check-out dates\n\nExample: "Hilton Hotel in Singapore from 15th March to 18th March"`,
      userEmail,
      userName
    }
  }];
}

// Date parsing with multiple formats
function parseDate(text) {
  const monthMap = {
    jan: 0, january: 0, feb: 1, february: 1, mar: 2, march: 2,
    apr: 3, april: 3, may: 4, jun: 5, june: 5,
    jul: 6, july: 6, aug: 7, august: 7, sep: 8, september: 8,
    oct: 9, october: 9, nov: 10, november: 10, dec: 11, december: 11
  };

  // Format: "15th March" or "March 15"
  const dateRegex = /(\d{1,2})(st|nd|rd|th)?\s+(jan|january|feb|february|mar|march|apr|april|may|jun|june|jul|july|aug|august|sep|september|oct|october|nov|november|dec|december)|(jan|january|feb|february|mar|march|apr|april|may|jun|june|jul|july|aug|august|sep|september|oct|october|nov|november|dec|december)\s+(\d{1,2})(st|nd|rd|th)?/gi;

  const matches = [...text.matchAll(dateRegex)];
  const dates = [];
  const currentYear = new Date().getFullYear();

  matches.forEach(match => {
    const day = parseInt(match[1] || match[6]);
    const monthStr = (match[3] || match[4]).toLowerCase();
    const month = monthMap[monthStr];

    if (day && month !== undefined) {
      dates.push(new Date(currentYear, month, day));
    }
  });

  return dates.sort((a, b) => a - b);
}

const dates = parseDate(query);
let checkInDate = null;
let checkOutDate = null;

if (dates.length >= 2) {
  checkInDate = dates[0];
  checkOutDate = dates[1];
} else if (dates.length === 1) {
  checkInDate = dates[0];
  // Default 3-night stay
  checkOutDate = new Date(checkInDate);
  checkOutDate.setDate(checkOutDate.getDate() + 3);
}

// Extract city
const cityPattern = /\b(in|at|near)\s+([a-z\s]{3,20})(?=\s+from|\s+check|,|$)/i;
const cityMatch = query.match(cityPattern);
let city = cityMatch ? cityMatch[2].trim() : '';

// Common cities fallback
const commonCities = ['singapore', 'mumbai', 'delhi', 'bangalore', 'goa', 'london', 'paris', 'dubai', 'new york', 'tokyo', 'bangkok'];
city = city || commonCities.find(c => query.toLowerCase().includes(c)) || '';

// Extract hotel name
let hotelName = query
  .replace(/from\s+\d/gi, '')
  .replace(/to\s+\d/gi, '')
  .replace(/check[- ]?in/gi, '')
  .replace(/check[- ]?out/gi, '')
  .replace(/\d{1,2}(st|nd|rd|th)?\s+\w+/gi, '')
  .replace(new RegExp(`\\b(in|at|near)\\s+${city}\\b`, 'gi'), '')
  .replace(/hotel|room|price|want|need|know|please|for|dates?/gi, '')
  .trim();

// Clean hotel name
hotelName = hotelName.split(/[,.]/).filter(p => p.trim().length > 2)[0]?.trim() || '';

// Validation
const errors = [];
if (!hotelName || hotelName.length < 3) errors.push('hotel name');
if (!city) errors.push('city');
if (!checkInDate) errors.push('check-in date');
if (!checkOutDate) errors.push('check-out date');

if (errors.length > 0) {
  return [{
    json: {
      status: 'missing_info',
      response: `I need more information. Please provide: ${errors.join(', ')}.\n\nExample: "Marriott Hotel in Dubai from 20th December to 23rd December"`,
      userEmail,
      userName
    }
  }];
}

// Format dates
const formatDate = (date) => {
  const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
  return `${date.getDate()} ${months[date.getMonth()]} ${date.getFullYear()}`;
};

const nights = Math.ceil((checkOutDate - checkInDate) / (1000 * 60 * 60 * 24));

return [{
  json: {
    status: 'ready',
    hotelName: hotelName.split(' ').map(w => w.charAt(0).toUpperCase() + w.slice(1).toLowerCase()).join(' '),
    city: city.split(' ').map(w => w.charAt(0).toUpperCase() + w.slice(1).toLowerCase()).join(' '),
    checkIn: formatDate(checkInDate),
    checkOut: formatDate(checkOutDate),
    checkInISO: checkInDate.toISOString().split('T')[0],
    checkOutISO: checkOutDate.toISOString().split('T')[0],
    nights,
    userEmail,
    userName,
    originalQuery: query
  }
}];

// Aggregate and compare prices from all platforms
const items = $input.all();
const searchData = items[0].json;

const prices = [];
const errors = [];

// Process each platform result
items.slice(1).forEach((item, index) => {
  const platforms = ['Booking.com', 'Agoda', 'Expedia'];
  const platform = platforms[index];

  if (item.json.error) {
    errors.push(`${platform}: Unable to fetch`);
  } else if (item.json.price) {
    prices.push({
      platform,
      price: parseFloat(item.json.price),
      currency: item.json.currency || 'USD',
      roomType: item.json.roomType || 'Standard Room',
      url: item.json.url || '#',
      availability: item.json.availability !== false
    });
  }
});

// Sort by price
prices.sort((a, b) => a.price - b.price);

if (prices.length === 0) {
  return [{
    json: {
      status: 'no_results',
      message: 'Unable to find prices. The hotel might not be available for these dates.',
      errors,
      ...searchData
    }
  }];
}

const bestDeal = prices[0];
const avgPrice = prices.reduce((sum, p) => sum + p.price, 0) / prices.length;
const savings = prices.length > 1 ? prices[prices.length - 1].price - bestDeal.price : 0;

return [{
  json: {
    status: 'success',
    ...searchData,
    results: prices,
    bestDeal,
    avgPrice: Math.round(avgPrice),
    savings: Math.round(savings),
    totalResults: prices.length,
    errors
  }
}];

