const articles = []

for (const item of $input.all()) {
  for(const article of item.json.articles){
    articles.push({
      json: { 
        name: article.split('/').filter(Boolean).join('-'),
        articleUri: $('set-website').first().json.website + article 
      }, 
      pairedItem: { item: item.binary }
    })
  }
}

return articles
