// Enhanced Flight Request Parser with NLP
const body = $input.first().json.body || {};
const query = body.message || body.query || '';

// Extract user details
const userEmail = body.email || body.user_email || '';
const userName = body.name || body.user_name || 'Traveler';
const notifyPriceDrop = body.notify_price_drop || false;

// Greeting handler
const greetings = ['hi', 'hello', 'hey', 'start', 'help'];
if (greetings.some(g => query.toLowerCase().includes(g)) && query.split(' ').length < 5) {
  return [{
    json: {
      status: 'greeting',
      response: `Hi ${userName}! ‚úàÔ∏è I'm your Flight Price Comparison Assistant.\n\nTell me:\n‚úÖ From city/airport\n‚úÖ To city/airport\n‚úÖ Departure date\n‚úÖ Trip type (one-way/round-trip)\n\nExample: "Flight from New York to London on 25th March, one-way"`,
      userEmail,
      userName
    }
  }];
}

// Airport/City codes mapping
const airportCodes = {
  'new york': 'JFK', 'nyc': 'JFK', 'london': 'LHR', 'paris': 'CDG',
  'dubai': 'DXB', 'singapore': 'SIN', 'tokyo': 'NRT', 'mumbai': 'BOM',
  'delhi': 'DEL', 'bangalore': 'BLR', 'los angeles': 'LAX', 'chicago': 'ORD',
  'san francisco': 'SFO', 'boston': 'BOS', 'miami': 'MIA', 'sydney': 'SYD',
  'melbourne': 'MEL', 'hong kong': 'HKG', 'bangkok': 'BKK', 'amsterdam': 'AMS',
  'frankfurt': 'FRA', 'toronto': 'YYZ', 'vancouver': 'YVR', 'seattle': 'SEA'
};

function getAirportCode(text) {
  const lower = text.toLowerCase().trim();
  if (/^[A-Z]{3}$/i.test(text)) return text.toUpperCase();
  return airportCodes[lower] || text.toUpperCase();
}

// Parse dates
function parseDate(text) {
  const monthMap = {
    jan: 0, january: 0, feb: 1, february: 1, mar: 2, march: 2,
    apr: 3, april: 3, may: 4, jun: 5, june: 5,
    jul: 6, july: 6, aug: 7, august: 7, sep: 8, september: 8,
    oct: 9, october: 9, nov: 10, november: 10, dec: 11, december: 11
  };

  const dateRegex = /(\d{1,2})(st|nd|rd|th)?\s+(jan|january|feb|february|mar|march|apr|april|may|jun|june|jul|july|aug|august|sep|september|oct|october|nov|november|dec|december)|\d{4}-\d{2}-\d{2}/gi;
  const matches = [...text.matchAll(dateRegex)];
  const dates = [];
  const currentYear = new Date().getFullYear();

  matches.forEach(match => {
    if (match[0].includes('-')) {
      dates.push(new Date(match[0]));
    } else {
      const day = parseInt(match[1]);
      const monthStr = match[3].toLowerCase();
      const month = monthMap[monthStr];
      if (day && month !== undefined) {
        dates.push(new Date(currentYear, month, day));
      }
    }
  });

  return dates.sort((a, b) => a - b);
}

// Extract origin and destination
let origin = '';
let destination = '';

const routePattern = /(?:from|leaving)?\s*([a-z\s]{3,25})\s+to\s+([a-z\s]{3,25})/i;
const routeMatch = query.match(routePattern);

if (routeMatch) {
  origin = routeMatch[1].trim();
  destination = routeMatch[2].trim();
} else {
  origin = body.from || body.origin || body.departure_airport || '';
  destination = body.to || body.destination || body.arrival_airport || '';
}

const originCode = getAirportCode(origin);
const destinationCode = getAirportCode(destination);

const dates = parseDate(query + ' ' + (body.departure_date || ''));
let departureDate = dates[0] || null;
let returnDate = dates[1] || null;

let tripType = 'one-way';
if (query.match(/round[\s-]?trip|return/i) || returnDate) {
  tripType = 'round-trip';
}
if (body.trip_type) {
  tripType = body.trip_type.toLowerCase();
}

const passengers = body.passengers || 1;
const cabinClass = body.class || body.cabin_class || 'economy';

const errors = [];
if (!originCode || originCode.length < 3) errors.push('departure city/airport');
if (!destinationCode || destinationCode.length < 3) errors.push('arrival city/airport');
if (!departureDate) errors.push('departure date');
if (tripType === 'round-trip' && !returnDate) errors.push('return date');

if (errors.length > 0) {
  return [{
    json: {
      status: 'missing_info',
      response: `I need more information: ${errors.join(', ')}.\n\nExample: "Flight from Mumbai to Dubai on 15th March, round-trip returning 20th March"`,
      userEmail,
      userName
    }
  }];
}

const formatDate = (date) => {
  if (!date) return null;
  const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
  return `${date.getDate()} ${months[date.getMonth()]} ${date.getFullYear()}`;
};

const formatDateISO = (date) => {
  if (!date) return null;
  return date.toISOString().split('T')[0];
};

return [{
  json: {
    status: 'ready',
    origin: originCode,
    destination: destinationCode,
    originCity: origin,
    destinationCity: destination,
    departureDate: formatDate(departureDate),
    returnDate: returnDate ? formatDate(returnDate) : null,
    departureDateISO: formatDateISO(departureDate),
    returnDateISO: formatDateISO(returnDate),
    tripType,
    passengers,
    cabinClass,
    userEmail,
    userName,
    notifyPriceDrop,
    originalQuery: query
  }
}];

// Aggregate flight prices from all platforms
const items = $input.all();
const searchData = items[0].json;

const flights = [];
const errors = [];

const platforms = ['Kayak', 'Skyscanner', 'Expedia', 'Google Flights'];

items.slice(1).forEach((item, index) => {
  const platform = platforms[index];
  const output = item.json.stdout || '';

  if (item.json.stderr || !output) {
    errors.push(`${platform}: Unable to fetch`);
    return;
  }

  const lines = output.trim().split('\n');

  lines.forEach(line => {
    if (line.includes('|')) {
      const parts = line.split('|');
      if (parts.length >= 6) {
        const price = parseFloat(parts[1].replace(/[^0-9.]/g, ''));

        if (price && price > 0) {
          flights.push({
            platform,
            airline: parts[0].trim(),
            price,
            currency: parts[1].match(/[A-Z‚Çπ$‚Ç¨¬£]/)?.[0] || '$',
            duration: parts[2].trim(),
            stops: parts[3].trim(),
            departureTime: parts[4].trim(),
            arrivalTime: parts[5].trim(),
            bookingUrl: parts[6]?.trim() || '#',
            cabinClass: searchData.cabinClass
          });
        }
      }
    }
  });
});

if (flights.length === 0) {
  return [{
    json: {
      status: 'no_results',
      message: 'No flights found for your search criteria.',
      errors,
      ...searchData
    }
  }];
}

flights.sort((a, b) => a.price - b.price);

const bestDeal = flights[0];
const avgPrice = flights.reduce((sum, f) => sum + f.price, 0) / flights.length;
const maxPrice = flights[flights.length - 1].price;
const savings = maxPrice - bestDeal.price;

const directFlights = flights.filter(f => f.stops === '0' || f.stops.toLowerCase().includes('non'));
const bestDirectFlight = directFlights.length > 0 ? directFlights[0] : null;

return [{
  json: {
    status: 'success',
    ...searchData,
    results: flights,
    bestDeal,
    bestDirectFlight,
    avgPrice: Math.round(avgPrice),
    maxPrice: Math.round(maxPrice),
    savings: Math.round(savings),
    totalResults: flights.length,
    directFlightsCount: directFlights.length,
    errors,
    searchTimestamp: new Date().toISOString()
  }
}];

// Format email report
const data = $input.first().json;

if (data.status === 'no_results') {
  return [{
    json: {
      subject: `‚ùå No Flights - ${data.origin} to ${data.destination}`,
      text: `No flights found for ${data.origin} to ${data.destination} on ${data.departureDate}`,
      ...data
    }
  }];
}

const { origin, destination, departureDate, returnDate, tripType, passengers, results, bestDeal, avgPrice, savings } = data;

const topResults = results.slice(0, 10);
const resultsText = topResults.map((f, i) => 
  `${i + 1}. ${f.airline} - ${f.currency}${f.price} (${f.stops === '0' ? 'Non-stop' : f.stops + ' stop(s)'}) - ${f.platform}`
).join('\n');

const textReport = `
FLIGHT PRICE COMPARISON
${'='.repeat(50)}

Route: ${origin} ‚Üí ${destination}
Departure: ${departureDate}
${returnDate ? `Return: ${returnDate}\n` : ''}Trip Type: ${tripType}
Passengers: ${passengers}

üèÜ BEST DEAL
${'-'.repeat(50)}
${bestDeal.airline}
Price: ${bestDeal.currency}${bestDeal.price}
Duration: ${bestDeal.duration}
Stops: ${bestDeal.stops === '0' ? 'Non-stop' : bestDeal.stops + ' stop(s)'}
Platform: ${bestDeal.platform}
${savings > 0 ? `\nüí∞ Save ${bestDeal.currency}${savings} vs highest price!` : ''}

üìä ALL RESULTS (Top 10)
${'-'.repeat(50)}
${resultsText}

Average Price: ${bestDeal.currency}${avgPrice}
Total Results: ${results.length}

Prices subject to availability.
Happy travels! ‚úàÔ∏è
`;

return [{
  json: {
    subject: `‚úàÔ∏è ${origin} ‚Üí ${destination} - Best: ${bestDeal.currency}${bestDeal.price}`,
    text: textReport,
    ...data
  }
}];
