// La salida del Merge Node junta ambos nodos
const current = $items()[0].json.stdout.trim();
const latest = $items()[1].json.tag_name.trim();

// Normalizamos
const c = current.replace(/^n8n@/, "").replace(/^v/, "");
const l = latest.replace(/^n8n@/, "").replace(/^v/, "");

return [{
  json: {
    current: c,
    latest: l,
    updateAvailable: c !== l
  }
}];

