function extractAddressData(text) {
  const regex = /Street Address:\s*([^<]+)<br>Street Address Line 2:\s*([^<]+)<br>City:\s*([^<]+)<br>State \/ Province:\s*([^<]+)<br>Postal \/ Zip Code:\s*([^<]+)<br>Country:\s*([^<]+)<br>/;
  const matches = text.match(regex);

  if (matches) {
    return {
      line1: matches[1].trim(),
      line2: matches[2].trim(),
      city: matches[3].trim(),
      stateProvince: matches[4].trim(),
      postalZipCode: matches[5].trim(),
      country: matches[6].trim()
    };
  }

  return {
    line1: null,
    line2: null,
    city: null,
    stateProvince: null,
    postalZipCode: null,
    country: null
  }
}

return {
  address: extractAddressData($input.first().json.body.billingAddress),
  customer: {
    name: $input.first().json.body.name,
    email: $input.first().json.body.email,
    phone: $input.first().json.body.phone
  },
  item: {
    name: $input.first().json.body.itemName
  }
}

