// Process Offset - Complete solution with time window and duplicate prevention
const results = items[0].json.result || [];
const staticData = $getWorkflowStaticData('global');

// Initialize if needed
if (!staticData.processed_ids) {
  staticData.processed_ids = [];
  staticData.last_offset = 0;
}

// Get previous offset
const previousOffset = staticData.last_offset || 0;

// Current time (Unix timestamp)
const currentTime = Math.floor(Date.now() / 1000);
const timeWindowSeconds = 15; // Slightly larger window for delays

// Debug info
console.log('=== Process Offset Debug ===' );
console.log('Previous offset:', previousOffset);
console.log('Number of messages received:', results.length);
console.log('Number of processed IDs:', staticData.processed_ids.length);

// ALWAYS update offset if there are results
if (results.length > 0) {
  const maxUpdateId = Math.max(...results.map(r => r.update_id));
  staticData.last_offset = maxUpdateId + 1;
  console.log('Offset updated:', staticData.last_offset);
}

// Filter: only new messages + time window + not yet processed
const newMessages = results.filter(msg => {
  // Check if already processed
  if (staticData.processed_ids.includes(msg.update_id)) {
    console.log(`Message ${msg.update_id} already processed, skipping`);
    return false;
  }

  // Check time window
  const messageTime = msg.channel_post?.date || 0;
  const timeDiff = currentTime - messageTime;

  if (timeDiff > timeWindowSeconds) {
    console.log(`Message ${msg.update_id} too old (${timeDiff}s), skipping`);
    return false;
  }

  // Check if newer than previous offset
  if (msg.update_id < previousOffset) {
    console.log(`Message ${msg.update_id} older than offset ${previousOffset}, skipping`);
    return false;
  }

  console.log(`Message ${msg.update_id} will be processed (${timeDiff}s old)`);
  return true;
});

// If there are new messages
if (newMessages.length > 0) {
  // Add processed IDs
  newMessages.forEach(msg => {
    staticData.processed_ids.push(msg.update_id);
  });

  // Cleanup: keep only last 100 IDs
  if (staticData.processed_ids.length > 100) {
    staticData.processed_ids = staticData.processed_ids.slice(-100);
  }

  console.log(`${newMessages.length} new messages to process`);

  // Return new messages WITH OFFSET
  return newMessages.map(msg => ({
    json: {
      ...msg,
      _current_offset: staticData.last_offset
    }
  }));
} else {
  console.log('No new messages within time window');

  // If no new messages, still return the offset
  return [{
    json: {
      _no_new_message: true,
      _current_offset: staticData.last_offset
    }
  }];
}

