// Expects each ticket to include: created_at (ISO), sla_due (ISO), id, priority
const out = items.map(i => {
  const t = i.json;
  const now = new Date();
  const created = new Date(t.created_at);
  const due = new Date(t.sla_due);
  const slaTotal = due - created; // ms
  const timeRemainingMs = due - now;
  const elapsed = slaTotal > 0 ? (1 - timeRemainingMs / slaTotal) : 1;
  const percentElapsed = Math.round(elapsed * 100);
  t.timeRemainingMinutes = Math.max(0, Math.round(timeRemainingMs / 60000));
  t.percentElapsed = Math.min(100, Math.max(0, percentElapsed));
  return { json: t };
});
return out;
