// Get all Jira issues from input
const items = $input.all().map(i => i.json);

// Initialize grouped output
const grouped = {};

// Iterate over each issue
for (const issue of items) {
  const f = issue.fields || {};

  const assigneeName = f.assignee?.displayName || 'Unassigned';
  const email = f.assignee?.emailAddress || 'user@example.com';
  const projectName = f.project?.name || 'Unknown Project';
  const sprint = f.customfield_10020?.[0]?.name || 'No Sprint';
  const timeSpentSeconds = f.timespent || 0;
  const timeSpentHours = (timeSpentSeconds / 3600).toFixed(2);

  if (!grouped[assigneeName]) {
    grouped[assigneeName] = {
      assignee: assigneeName,
      email,
      project_name: projectName,
      total_hours: 0,
      issues: []
    };
  }

  grouped[assigneeName].total_hours += parseFloat(timeSpentHours);
  grouped[assigneeName].issues.push({
    issue_key: issue.key,
    summary: f.summary,
    timespent_hours: timeSpentHours,
    status: f.status?.name || 'Unknown',
    priority: f.priority?.name || 'None',
    sprint,
  });
}

// Convert grouped object into array format
const output = Object.values(grouped).map(user => ({
  json: {
    assignee: user.assignee,
    email: user.email,
    project_name: user.project_name,
    total_hours: user.total_hours.toFixed(2),
    issues: user.issues,
  }
}));

return output;

const users = $input.all().map(i => i.json);

const reminders = [];

for (const user of users) {
  const missing = user.issues.filter(i => parseFloat(i.timespent_hours) === 0);

  if (missing.length > 0) {
    const table = missing.map(i => 
      `<tr>
        <td>${i.issue_key}</td>
        <td>${i.summary}</td>
        <td>${i.status}</td>
        <td>${i.priority}</td>
        <td>${i.sprint}</td>
      </tr>`
    ).join('');

    reminders.push({
      json: {
        assignee: user.assignee,
        email: user.email,
        missing_count: missing.length,
        project_name: user.project_name,
        html_message: `
          <p>Hi ${user.assignee},</p>
          <p>Our system found ${missing.length} issues where no time is logged:</p>
          <table border="1" cellspacing="0" cellpadding="5">
            <tr><th>Issue Key</th><th>Summary</th><th>Status</th><th>Priority</th><th>Sprint</th></tr>
            ${table}
          </table>
          <p>Please log your hours for these issues before EOD to ensure accurate billing.</p>
          <p>Thank you,<br>Automation Bot ü§ñ</p>
        `
      }
    });
  }
}

return reminders;

// Combined Invoice Generator + Text File Export

const users = $input.all().map(i => i.json);
const RATE = 50; // hourly rate
const results = [];

for (const user of users) {
  const totalHours = parseFloat(user.total_hours || 0);
  if (totalHours === 0) continue;

  const totalAmount = totalHours * RATE;

  // Build the plain text table
  const issueLines = user.issues.map(i =>
    `${i.issue_key.padEnd(10)} | ${i.summary.padEnd(30)} | ${i.timespent_hours} hrs | ${i.status}`
  ).join('\n');

  // Final plain-text invoice content
  const text = `
YOUR_AWS_SECRET_KEY_HERE===
INVOICE SUMMARY ‚Äî ${user.project_name}
YOUR_AWS_SECRET_KEY_HERE===

Assignee: ${user.assignee}
Email: ${user.email}
Rate: $${RATE}/hour

-------------------------------------------
Issues
-------------------------------------------
${issueLines}

-------------------------------------------
Total Hours: ${totalHours.toFixed(2)}
Total Amount: $${totalAmount.toFixed(2)}
-------------------------------------------

Generated automatically by Techdome Billing Automation Bot.
`;

  // Push JSON + Binary for email attachment
  results.push({
    json: {
      assignee: user.assignee,
      email: user.email,
      project_name: user.project_name,
      total_hours: totalHours.toFixed(2),
      total_amount: totalAmount.toFixed(2)
    },
    binary: {
      data: {
        fileName: `Invoice_${user.assignee}.txt`,
        mimeType: 'text/plain',
        data: Buffer.from(text, 'utf8').toString('base64')
      }
    }
  });
}

return results;

// Combine JSON + Binary after Merge node
// Safe for n8n v1.112.6 and later

const combined = [];

// Get all incoming items
const items = $input.all();

// We might have duplicates or partials, so we merge smartly
for (const item of items) {
  const json = item.json || {};
  const binary = item.binary || {};

  // Case 1: this item already has both
  if (json.email && binary.data) {
    combined.push({ json, binary });
    continue;
  }

  // Case 2: only has JSON
  if (json.email && !binary.data) {
    // Try to find its binary partner
    const match = items.find(
      (i) => i.binary && i.binary.data && i.json?.assignee === json.assignee
    );

    if (match) {
      combined.push({ json, binary: match.binary });
    } else {
      combined.push({ json }); // fallback
    }
    continue;
  }

  // Case 3: only has Binary
  if (binary.data && !json.email) {
    // Try to find its JSON partner
    const match = items.find(
      (i) => i.json && i.json.assignee && i.binary === undefined
    );
    if (match) {
      combined.push({ json: match.json, binary });
    } else {
      combined.push({ binary });
    }
    continue;
  }
}

return combined;
