// Merge markdown from all pages (works for any number of pages)
const pages = $json.pages;
let combinedMarkdown = "";

for (let i = 0; i < pages.length; i++) {
  if (pages[i]?.markdown) {
    combinedMarkdown += pages[i].markdown + "\n";
  }
}

return [{ json: { combined_markdown: combinedMarkdown } }];

const input = $input.first();
let data = input.json.message.content || input.json.content || input.json;

// Remove markdown ``` or ```
if (typeof data === 'string') {
  data = data.replace(/```+[\w]*\s*/g, '').trim();
}

// Parse JSON
try {
  data = JSON.parse(data);
} catch (err) {
  return [{
    json: {
      error: "Failed to parse JSON",
      message: err.message,
      raw: typeof data === 'string' ? data.substring(0, 500) : ''
    }
  }];
}

// Validate array
if (!Array.isArray(data)) {
  return [{
    json: {
      error: "Parsed data is not an array",
      data
    }
  }];
}

// Map each array entry into a separate n8n item
return data.map(item => {
  // Clean reference interval: keep only numbers, hyphens, dots, spaces
  let ref = item.reference_range || 'N/A';
  if (ref && ref !== 'N/A') {
    ref = ref.replace(/[^0-9.\- ]/g, '').replace(/\s*-\s*/g, ' - ').replace(/\s+/g, ' ').trim();
  }
  return {
    json: {
      'Diagnostic Centre': item.diagnostic_centre || 'N/A',
      'Name': item.patient_name || 'N/A',
      'Age': item.age || 'N/A',
      'Gender': item.gender || 'N/A',
      'Registered on': item.registration_date || 'N/A',
      'Sample Type': item.sample_type || 'N/A',
      'Test Name': item.test_name || 'N/A',
      'Result': item.result_value !== undefined ? String(item.result_value) : 'N/A',
      'Unit': item.unit || 'N/A',
      'Biological reference Interval': ref
    }
  };
});

function isOutOfRange(result, ref) {
  if (!result || !ref) return false;
  // strip units, trim spaces
  let val = parseFloat((result + '').replace(/[^0-9.\-]/g, ''));
  let match = ref.match(/^\s*(\d+\.?\d*)\s*-\s*(\d+\.?\d*)/); // "0.3-1.2" etc.
  if (match) {
    let low = parseFloat(match[1]);
    let high = parseFloat(match[2]);
    return (val < low || val > high);
  }
  if (ref.match(/^<\s*(\d+)/)) {
    let high = parseFloat(ref.match(/^<\s*(\d+)/)[1]);
    return (val >= high);
  }
  if (ref.match(/^>\s*(\d+)/)) {
    let low = parseFloat(ref.match(/^>\s*(\d+)/)[1]);
    return (val <= low);
  }
  return false; // fallback
}

let outOfRangeItems = [];
for (let item of items) {
  item.json['Dietary advice'] = '';
  item.json['Lifestyle advice'] = '';
  item.json['Exercise advice'] = '';
  if (isOutOfRange(item.json['Result'], item.json['Biological reference Interval'])) {
    outOfRangeItems.push(item);
  }
}
return outOfRangeItems; // This will go to the AI node for advice

// Merge AI Response Back - SIMPLE VERSION
// Assumes AI responses are in the same order as out-of-range items

console.log('=== SIMPLE MERGE START ===');

const aiItems = items; // AI advice responses
const allRows = $('Out-of-Range Detection & Advice Fields').all();

console.log('AI items:', aiItems.length);
console.log('Medical rows:', allRows.length);

// Process each row
for (let i = 0; i < allRows.length; i++) {
  const row = allRows[i];

  let dietary = '';
  let lifestyle = '';
  let exercise = '';

  // If we have AI advice for this index
  if (i < aiItems.length) {
    const ai = aiItems[i];

    // Get content from various possible locations
    let content = ai.json.content || ai.json.message?.content || ai.json.text || '';

    if (content) {
      try {
        // Remove code fences
        content = content.replace(/```(?:json)?\s*/g, '').replace(/```/g, '').trim();

        // Parse JSON
        const obj = JSON.parse(content);

        dietary = obj["Dietary advice"] || obj["dietary_advice"] || '';
        lifestyle = obj["Lifestyle advice"] || obj["lifestyle_advice"] || '';
        exercise = obj["Exercise advice"] || obj["exercise_advice"] || '';

        console.log(`Row ${i}: Successfully extracted advice`);

      } catch (err) {
        console.error(`Row ${i}: Parse error -`, err.message);
      }
    }
  }

  row.json['Dietary advice'] = dietary;
  row.json['Lifestyle advice'] = lifestyle;
  row.json['Exercise advice'] = exercise;
}

console.log('=== SIMPLE MERGE COMPLETE ===');
return allRows;

