const jotformData = items[0].json;
const customerName = jotformData['Customer Name'] || 'Customer';
const rating = parseInt(jotformData.Rating) || 3;

const namePart = customerName.substring(0, 5).replace(/\s/g, '');
const randomNum = Math.floor(Math.random() * 9999);
const couponCode = 'THANKS' + namePart + randomNum;

let discountPercent = 10;
if (rating === 5) {
  discountPercent = 20;
} else if (rating === 4) {
  discountPercent = 15;
}

const expiryDate = new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().slice(0, 10);

return [{
  json: {
    couponCode: couponCode,
    discountPercent: discountPercent,
    expiryDays: 30,
    expiryDate: expiryDate,
    customerName: customerName,
    customerEmail: jotformData['Customer email'],
    rating: rating
  }
}];
