parameters.jsCode:
const income = $('Extract Application Data').first().json.monthlyIncome;
const debts = $('Extract Application Data').first().json.monthlyDebts;
const dti = $('Parse AI Analysis').first().json.debtToIncomeRatio;

let creditScore = 720;
if (dti < 0.30 && income > 8000) creditScore = 760;
else if (dti > 0.43 || income < 4000) creditScore = 650;

const creditData = {
  creditScore: creditScore,
  paymentHistory: creditScore > 700 ? 'excellent' : creditScore > 650 ? 'good' : 'fair',
  creditUtilization: creditScore > 700 ? 25 : 45,
  recentInquiries: creditScore > 700 ? 1 : 3,
  creditCheckDate: new Date().toISOString(),
  ...$('Parse AI Analysis').first().json,
  ...$('Extract Application Data').first().json
};

return [{ json: creditData }];
