// A parancs eredménye a bemenet json stdout mezőjében
const output = items[0].json.stdout;

// Regex a Last Update kinyeréséhez
const lastUpdateMatch = output.match(/Last Update:\s*(.+)/);
const lastUpdate = lastUpdateMatch ? lastUpdateMatch[1].trim() : null;

// Regex a Next Update kinyeréséhez
const nextUpdateMatch = output.match(/Next Update:\s*(.+)/);
const nextUpdate = nextUpdateMatch ? nextUpdateMatch[1].trim() : null;

// Regex a CN kinyeréséhez az Issuer szövegből
const issuerMatch = output.match(/Issuer:(.+)/);
let cn = null;
if (issuerMatch) {
  // issuer szöveg, például: " C=HU, L=Budapest, O=NetLock Kft., CN=NetLock Közigazgatási (Class B) Tanúsítványkiadó, emailAddress=user@example.com"
  const issuer = issuerMatch[1];
  const cnMatch = issuer.match(/CN=([^,]+)/);
  if (cnMatch) {
    cn = cnMatch[1].trim();
  }
}

return [
  {
    json: {
      lastUpdate,
      nextUpdate,
      issuerCN: cn
    }
  }
];

const lastUpdate = items[0].json.lastUpdate;
const nextUpdate = items[0].json.nextUpdate;
const issuerCN = items[0].json.issuerCN;

// A "nextUpdate" stringből Date objektum készítése
const nextUpdateDate = new Date(nextUpdate + ' UTC'); // Hozzáadva az UTC időzóna

const now = new Date();

// Különbség milliszekundumban
const diffMs = nextUpdateDate.getTime() - now.getTime();

// Különbség órákban
const diffHours = diffMs / (1000 * 60 * 60);

let crlStatus = null;
if (diffHours < 17) {
  crlStatus = "ALERT!";
}
else {
  crlStatus = "READY!";
}

return [
  {
    json: {
      lastUpdate,
      nextUpdate,
      issuerCN,
      crlStatus
    }
  }
];

const notBefore = items[0].json.notBefore;
const notAfter = items[0].json.notAfter;
const subjectCN = items[0].json.subjectCN;

const notAfterDate = new Date(notAfter + ' UTC');
const now = new Date();

let caStatus = null;

if (now > notAfterDate) {
  caStatus = "EXPIRED!";
} else {
  const diffMs = notAfterDate.getTime() - now.getTime();
  const diffHours = diffMs / (1000 * 60 * 60);

  if (diffHours < 17) {
    caStatus = "ALERT!";
  } else {
    caStatus = "READY!";
  }
}

return [
  {
    json: {
      notBefore,
      notAfter,
      subjectCN,
      caStatus
    }
  }
];

const output = items[0].json.stdout;

const notBeforeMatch = output.match(/Not Before:\s*(.+)/);
const notBefore = notBeforeMatch ? notBeforeMatch[1].trim() : null;

const notAfterMatch = output.match(/Not After\s*:\s*(.+)/);
const notAfter = notAfterMatch ? notAfterMatch[1].trim() : null;

const subjectMatch = output.match(/Subject:(.+)/);
let cn = null;
if (subjectMatch) {
  const subject = subjectMatch[1];
  const cnMatch = subject.match(/CN=([^,]+)/);
  if (cnMatch) {
    cn = cnMatch[1].trim();
  }
}

return [
  {
    json: {
      notBefore,
      notAfter,
      subjectCN: cn
    }
  }
];
