// Calculate date range for API request
const today = new Date();
const nextWeek = new Date();
nextWeek.setDate(today.getDate() + 14);

// Format dates as YYYY-MM-DD
const formatDate = (date) => {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
};

return [{
  json: {
    start_date: formatDate(today),
    end_date: formatDate(nextWeek),
    today: formatDate(today),
    next_week: formatDate(nextWeek)
  }
}];

// Get all individual asteroid items from the input
const allAsteroidItems = $input.all();

// Configuration thresholds („ÉÜ„Çπ„ÉàÁî®„ÅÆÁ∑©„ÅÑÂÄ§)
const MAX_DISTANCE_KM = 75000000000; // 75 billion km
const MIN_DIAMETER_METERS = 1;       // 1 meter

const filteredAsteroids = [];

// Loop through each asteroid item that was passed to this node
for (const item of allAsteroidItems) {
  const asteroid = item.json; // Get the actual asteroid data from the item

  // Ensure the necessary data exists before trying to process it
  if (asteroid && asteroid.close_approach_data && asteroid.close_approach_data.length > 0) {
    const missDistance = parseFloat(asteroid.close_approach_data[0].miss_distance.kilometers);
    const maxDiameter = asteroid.estimated_diameter.meters.estimated_diameter_max;
    const minDiameter = asteroid.estimated_diameter.meters.estimated_diameter_min;
    const avgDiameter = (maxDiameter + minDiameter) / 2;

    // Apply filters
    if (missDistance <= MAX_DISTANCE_KM && avgDiameter >= MIN_DIAMETER_METERS) {
      filteredAsteroids.push({
        name: asteroid.name,
        id: asteroid.id,
        approach_date: asteroid.close_approach_data[0].close_approach_date,
        approach_date_full: asteroid.close_approach_data[0].close_approach_date_full,
        miss_distance_km: Math.round(missDistance),
        miss_distance_lunar: parseFloat(asteroid.close_approach_data[0].miss_distance.lunar).toFixed(2),
        diameter_min_m: Math.round(minDiameter),
        diameter_max_m: Math.round(maxDiameter),
        diameter_avg_m: Math.round(avgDiameter),
        velocity_km_h: Math.round(parseFloat(asteroid.close_approach_data[0].relative_velocity.kilometers_per_hour)),
        is_potentially_hazardous: asteroid.is_potentially_hazardous_asteroid,
        nasa_jpl_url: asteroid.nasa_jpl_url,
        absolute_magnitude: asteroid.absolute_magnitude_h
      });
    }
  }
}

// Sort by closest approach distance
filteredAsteroids.sort((a, b) => a.miss_distance_km - b.miss_distance_km);

// Return the final list of asteroids that met the criteria
return filteredAsteroids.map(asteroid => ({ json: asteroid }));

// Format message for Slack/Email
const asteroids = $input.all();
const messageLines = [];

messageLines.push('üö® *NEAR-EARTH ASTEROID ALERT* üö®');
messageLines.push('');
messageLines.push(`Found ${asteroids.length} asteroid(s) meeting alert criteria:`);
messageLines.push('');

asteroids.forEach((item, index) => {
  const asteroid = item.json;
  const hazardIcon = asteroid.is_potentially_hazardous ? '‚ö†Ô∏è' : '‚úì';

  messageLines.push(`*${index + 1}. ${asteroid.name}* ${hazardIcon}`);
  messageLines.push(`   ‚Ä¢ Approach Date: ${asteroid.approach_date}`);
  messageLines.push(`   ‚Ä¢ Distance: ${asteroid.miss_distance_km.toLocaleString()} km (${asteroid.miss_distance_lunar} lunar distances)`);
  messageLines.push(`   ‚Ä¢ Size: ${asteroid.diameter_min_m}-${asteroid.diameter_max_m} meters`);
  messageLines.push(`   ‚Ä¢ Speed: ${asteroid.velocity_km_h.toLocaleString()} km/h`);
  messageLines.push(`   ‚Ä¢ Details: ${asteroid.nasa_jpl_url}`);
  messageLines.push('');
});

messageLines.push('_Data source: NASA Near Earth Object Web Service_');

return [{
  json: {
    slackMessage: messageLines.join('\n'),
    emailSubject: `üö® Asteroid Alert: ${asteroids.length} Near-Earth Object(s) Detected`,
    emailBody: messageLines.join('\n').replace(/\*/g, '').replace(/_/g, ''),
    asteroidCount: asteroids.length,
    asteroids: asteroids.map(a => a.json)
  }
}];
