return $input.all().map(item => {
  const body = item.json.body;

  const customer = {
    first_name: body.billing.first_name,
    last_name: body.billing.last_name,
    email: body.billing.email,
    phone: body.billing.phone,
    address: {
      address_1: body.billing.address_1,
      address_2: body.billing.address_2,
      city: body.billing.city,
      state: body.billing.state,
      postcode: body.billing.postcode,
      country: body.billing.country
    }
  };

  const products = body.line_items.map(product => ({
    status: body.status,
    name: product.name,
    size: product.meta_data?.find(meta => meta.key === "size")?.value,
    product_id: product.product_id,
    variation_id: product.variation_id,
    quantity: product.quantity,
    price: `${body.currency} ${product.price}`,
    total: `${body.currency} ${product.total}`,
    image: product.image?.src || null
  }));

  return {
    json: {
      data: {
        customer,
        products,
        invoice_link: body.payment_url
      }
    }
  };
});

const items = $input.all();
const updatedItems = items.map((item) => {
  let rawNumber = item?.json["number"];
  rawNumber = rawNumber ? String(rawNumber) : "";
  const cleanedNumber = rawNumber.replace(/\D/g, "");
  item.json["number"] = cleanedNumber;
  return item;
});
return updatedItems;

